import 'dart:io' show Directory;
import 'package:path/path.dart' show join;
import 'package:sqflite/sqflite.dart';
import 'package:path_provider/path_provider.dart' show getApplicationDocumentsDirectory;

class DatabaseHelper {

  static final _databaseName = "MyDatabase.db";
  static final _databaseVersion = 1;

  static final table = 'additivesTable';

  static final columnId = '_id';
  static final columnTitle = '_title';
  static final columnName = '_name';
  static final columnSuitableFor = '_suitablefor';
  static final columnDescription = '_description';
  static final columnCategory = '_category';
  static final columnStatus = '_status';


  // make this a singleton class
  DatabaseHelper._privateConstructor();
  static final DatabaseHelper instance = DatabaseHelper._privateConstructor();

  // only have a single app-wide reference to the database
  static Database _database;
  Future<Database> get database async {
    if (_database != null) return _database;
    // lazily instantiate the db the first time it is accessed
    _database = await _initDatabase();
    return _database;
  }

  // this opens the database (and creates it if it doesn't exist)
  _initDatabase() async {
    Directory documentsDirectory = await getApplicationDocumentsDirectory();
    String path = join(documentsDirectory.path, _databaseName);
    return await openDatabase(path,
        version: _databaseVersion,
        onCreate: _onCreate,

    );
  }

  // SQL code to create the database table
  Future _onCreate(Database db, int version) async {

    await db.execute('''
          CREATE TABLE $table (
            $columnId INTEGER PRIMARY KEY,
            $columnTitle TEXT,
            $columnName TEXT,
            $columnSuitableFor TEXT,
            $columnDescription TEXT,
            $columnCategory TEXT,
            $columnStatus TEXT
          )
          ''');


    /**********************COLOURS***************/
/*
    await db.rawInsert(
        'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',
        ['', '', '', '', '','']);
*/
//Acidity
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E599','Perlite','May or may not be vegan',"Perlite is an amorphous volcanic glass that has a relatively high water content, typically formed by the hydration of obsidian. It occurs naturally and has the unusual property of greatly expanding when heated sufficiently. It is an industrial mineral and a commercial product useful for its low density after processing.  ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E598','Synthetic calcium aluminates','May or may not be vegan',"Calcium aluminates are a range of minerals obtained by heating calcium oxide and aluminium oxide together at high temperatures. They are encountered in the manufacture of refractories and cements. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E586','4-Hexylresorcinol','May or may not be vegan',"Hexylresorcinol is an organic compound with local anaesthetic, antiseptic, and anthelmintic properties. ",'antioxidant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E585','Ferrous lactate','May or may not be vegan',"Ferrous lactate, or iron(II) lactate, is a chemical compound consisting of one atom of iron (Fe2+) and two lactate anions. It has the chemical formula Fe(C3H5O3)2. ",'food colouring','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E580','Magnesium gluconate','May or may not be vegan',"Gluconic acid is an organic compound with molecular formula C6H12O7 and condensed structural formula HOCH2(CHOH)4COOH. It is one of the 16 stereoisomers of 2,3,4,5,6-pentahydroxyhexanoic acid. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E579','Ferrous gluconate','Vegan',"Iron(II) gluconate, or ferrous gluconate, is a black compound often used as an iron supplement. It is the iron(II) salt of gluconic acid. It is marketed under brand names such as Fergon, Ferralet and Simron. ",'food colouring','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E578','Calcium gluconate','Vegan',"Calcium lactate gluconate, also known as GLOCAL, is a soluble salt of calcium, lactic acid and gluconic acid used in effervescent calcium tablets. Its chemical formula is Ca5(C3H5O3)6·(C6H11O7)4·2H2O. It was first developed by Sandoz, Switzerland. Calcium lactate gluconate is used in the functional and fortified food industry due to its good solubility and neutral taste. In addition, it is used in various spherification techniques in molecular gastronomy. It can also be used to help neutralize HF (hydrofluoric acid) poisoning. ",'firming agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E577','Potassium gluconate','Vegan',"Potassium gluconate is the potassium salt of the conjugate base of gluconic acid. It is also referred to as 2,3,4,5,6-pentahydroxycaproic acid potassium salt, D-gluconic acid potassium salt, or potassium D-gluconate. ",'sequestrant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E576','Sodium gluconate','Vegan',"Glucono delta-lactone (GDL), also known as gluconolactone, is a food additive with the E number E575 used as a sequestrant, an acidifier, or a curing, pickling, or leavening agent. It is a lactone of d-gluconic acid. Pure GDL is a white odorless crystalline powder. ",'sequestrant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E575','Glucono delta-lactone (acidity regulator)','Vegan',"Glucono delta-lactone (GDL), also known as gluconolactone, is a food additive with the E number E575 used as a sequestrant, an acidifier, or a curing, pickling, or leavening agent. It is a lactone of d-gluconic acid. Pure GDL is a white odorless crystalline powder. ",'sequestrant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E574','Gluconic acid','Vegan',"Gluconic acid is an organic compound with molecular formula C6H12O7 and condensed structural formula HOCH2(CHOH)4COOH. It is one of the 16 stereoisomers of 2,3,4,5,6-pentahydroxyhexanoic acid. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E572','Magnesium stearate, calcium stearate (emulsifier)','May or may not be vegan',"Stearic acid (/ˈstɪərɪk/ STEER-ik, /stiˈærɪk/ stee-ARR-ik) is a saturated fatty acid with an 18-carbon chain and has the IUPAC name octadecanoic acid. It is a waxy solid and its chemical formula is C17H35CO2H. Its name comes from the Greek word στέαρ \"stéar\", which means tallow. The salts and esters of stearic acid are called stearates. As its ester, stearic acid is one of the most common saturated fatty acids found in nature following palmitic acid. The triglyceride derived from three molecules of stearic acid is called stearin. ",'anti-caking agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E570','Fatty acids','May or may not be vegan',"Stearic acid (/ˈstɪərɪk/ STEER-ik, /stiˈærɪk/ stee-ARR-ik) is a saturated fatty acid with an 18-carbon chain and has the IUPAC name octadecanoic acid. It is a waxy solid and its chemical formula is C17H35CO2H. Its name comes from the Greek word στέαρ \"stéar\", which means tallow. The salts and esters of stearic acid are called stearates. As its ester, stearic acid is one of the most common saturated fatty acids found in nature following palmitic acid. The triglyceride derived from three molecules of stearic acid is called stearin. ",'anti-caking agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E566','Natrolite-phonolite','May or may not be vegan',"Lignosulfonates, or sulfonated lignin are water-soluble anionic polyelectrolyte polymers: they are byproducts from the production of wood pulp using sulfite pulping. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E565','Lignosulfonates','May or may not be vegan',"Lignosulfonates, or sulfonated lignin are water-soluble anionic polyelectrolyte polymers: they are byproducts from the production of wood pulp using sulfite pulping. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E563','Sepiolitic clay','May or may not be vegan',"Vermiculite, a hydrous phyllosilicate mineral, undergoes significant expansion when heated. Exfoliation occurs when the mineral is heated sufficiently, and commercial furnaces can routinely produce this effect. Vermiculite forms by the weathering or hydrothermal alteration of biotite or phlogopite. Large commercial vermiculite mines currently exist in Russia, South Africa, China, and Brazil. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E562','Sepiolite','May or may not be vegan',"Vermiculite, a hydrous phyllosilicate mineral, undergoes significant expansion when heated. Exfoliation occurs when the mineral is heated sufficiently, and commercial furnaces can routinely produce this effect. Vermiculite forms by the weathering or hydrothermal alteration of biotite or phlogopite. Large commercial vermiculite mines currently exist in Russia, South Africa, China, and Brazil. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E561','Vermiculite','May or may not be vegan',"Vermiculite, a hydrous phyllosilicate mineral, undergoes significant expansion when heated. Exfoliation occurs when the mineral is heated sufficiently, and commercial furnaces can routinely produce this effect. Vermiculite forms by the weathering or hydrothermal alteration of biotite or phlogopite. Large commercial vermiculite mines currently exist in Russia, South Africa, China, and Brazil. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E560','Potassium silicate','May or may not be vegan',"Potassium silicate is the name for a family of inorganic compounds. The most common potassium silicate has the formula K2SiO3, samples of which contain varying amounts of water. These are white solids or colorless solutions. ",'anti-caking agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E559','Aluminium silicate (Kaolin)','Vegan',"Aluminium silicate (or aluminum silicate) is a name commonly applied to chemical compounds which are derived from aluminium oxide, Al2O3 and silicon dioxide, SiO2 which may be anhydrous or hydrated, naturally occurring as minerals or synthetic. Their chemical formulae are often expressed as xAl2O3.ySiO2.zH2O. It is known as E number E559. ",'anti-caking agent','removed from list as per Commission Regulation (EU) No 380/2012 of 3 May 2012']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E558','Bentonite','Vegan',"Bentonite (/ˈbɛntənʌɪt/) is an absorbent aluminium phyllosilicate clay consisting mostly of montmorillonite. One of the first occurrences of bentonite was found in the Cretaceous Benton Shale near Rock River, Wyoming. The Fort Benton Group, along with others in stratigraphic succession, was named after Fort Benton, Montana in the mid-19th century by Fielding Bradford Meek and F. V. Hayden of the U.S. Geological Survey. ",'anti-caking agent','removed from list as per Commission Regulation (EU) No 380/2012 of 3 May 2012']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E557','Zinc silicate','May or may not be vegan',"Hemimorphite, is Zn4(Si2O7)(OH)2·H2O, a component of calamine. It is a sorosilicate mineral which has been historically mined from the upper parts of zinc and lead ores, chiefly associated with smithsonite, ZnCO3. They were assumed to be the same mineral and both were classed under the same name of calamine. In the second half of the 18th century it was discovered that these two different minerals were both present in calamine. They closely resemble each other. ",'anti-caking agent','removed from list as per Commission Regulation (EU) No 380/2012 of 3 May 2012']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E556','Calcium aluminosilicate (calcium aluminium silicate)','Vegan',"Calcium aluminosilicate, an aluminosilicate compound with calcium cations, most typically has formula CaAl2Si2O8. In minerals, as a feldspar, it can be found as anorthite, an end-member of the plagioclase series. ",'anti-caking agent','removed from list as per Commission Regulation (EU) No 380/2012 of 3 May 2012']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E555','Potassium aluminium silicate','Vegan',"Microcline (KAlSi3O8) is an important igneous rock-forming tectosilicate mineral. It is a potassium-rich alkali feldspar. Microcline typically contains minor amounts of sodium. It is common in granite and pegmatites. Microcline forms during slow cooling of orthoclase; it is more stable at lower temperatures than orthoclase. Sanidine is a polymorph of alkali feldspar stable at yet higher temperature. Microcline may be clear, white, pale-yellow, brick-red, or green; it is generally characterized by cross-hatch twinning that forms as a result of the transformation of monoclinic orthoclase into triclinic microcline. ",'anti-caking agent','removed from list as per Commission Regulation (EU) No 380/2012 of 3 May 2012']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E554','Sodium aluminosilicate (sodium aluminium silicate)','Vegan',"Sodium aluminosilicate refers to compounds which contain sodium, aluminium, silicon and oxygen, and which may also contain water. These include synthetic amorphous sodium aluminosilicate, a few naturally occurring minerals and synthetic zeolites. Synthetic amorphous sodium aluminosilicate is widely used as a food additive, E 554. ",'anti-caking agent','removed from list as per Commission Regulation (EU) No 380/2012 of 3 May 2012']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E553b','Talc','Vegan',"Talc is a clay mineral composed of hydrated magnesium silicate with the chemical formula Mg3Si4O10(OH)2. Talc in powdered form, often in combination with corn starch, is widely used as baby powder. This mineral is used as a thickening agent and lubricant, is an ingredient in ceramics, paint and roofing material, and is also one of the main ingredients in many cosmetic products. It occurs as foliated to fibrous masses, and in an exceptionally rare crystal form. It has a perfect basal cleavage, uneven flat fracture and it is foliated with a two dimensional platy form. ",'anti-caking agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E553a','(i) Magnesium silicate (ii) Magnesium trisilicate','May or may not be vegan',"Magnesium trisilicate is an inorganic compound that is used as a food additive. The additive is frequently used by fast food chains to absorb fatty acids and extract impurities formed while frying edible oils. It has good acid neutralizing properties, but the reaction appears too slow to serve as an effective non-prescription antacid. ",'anti-caking agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E552','Calcium silicate','Vegan',"Calcium silicate is the chemical compound Ca2SiO4, also known as calcium orthosilicate and is sometimes formulated as 2CaO·SiO2. It is also referred to by the shortened trade name Cal-Sil or Calsil. ",'anti-caking agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E551','Silicon dioxide (Silica)','Vegan',"Silicon dioxide, also known as silica, is an oxide of silicon with the chemical formula SiO2, most commonly found in nature as quartz and in various living organisms. In many parts of the world, silica is the major constituent of sand. Silica is one of the most complex and most abundant families of materials, existing as a compound of several minerals and as synthetic product. Notable examples include fused quartz, fumed silica, silica gel, and aerogels. It is used in structural materials, microelectronics (as an electrical insulator), and as components in the food and pharmaceutical industries. ",'anti-caking agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E550','Sodium Silicates (i) Sodium silicate (ii) Sodium metasilicate','Vegan',"Sodium silicate is a generic name for chemical compounds with the formula Na2xSiyO2y+x or (Na2O)x·(SiO2)y, such as sodium metasilicate Na2SiO3, sodium orthosilicate Na4SiO4, and sodium pyrosilicate Na6Si2O7. The anions are often polymeric. These compounds are generally colorless transparent solids or white powders, and soluble in water in various amounts. ",'anti-caking agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E545','Ammonium polyphosphate','Vegan',"Ammonium polyphosphate commercially produced by Clariant, (former business area of Hoechst AG), Budenheim and other sources is an inorganic salt of polyphosphoric acid and ammonia containing both chains and possibly branching. Its chemical formula is [NH4 PO3]n(OH)2 showing that each monomer consists of an orthophosphate radical of a phosphorus atom with three oxygens and one negative charge neutralized by an ammonium cation leaving two bonds free to polymerize. In the branched cases some monomers are missing the ammonium anion and instead link to three other monomers. ",'emulsifier','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E544','Calcium polyphosphate','Vegan',"Sodium aluminium phosphate (SAlP) describes the inorganic compounds consisting of sodium salts of aluminium phosphates. The most common SAlP has the formulas NaH14Al3(PO4)8·4H2O and Na3H15Al2(PO4)8. These materials are prepared by combining alumina, phosphoric acid, and sodium hydroxide. ",'emulsifier','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E543','Calcium sodium polyphosphate','Vegan',"Sodium aluminium phosphate (SAlP) describes the inorganic compounds consisting of sodium salts of aluminium phosphates. The most common SAlP has the formulas NaH14Al3(PO4)8·4H2O and Na3H15Al2(PO4)8. These materials are prepared by combining alumina, phosphoric acid, and sodium hydroxide. ",'emulsifier','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E542','Bone phosphate (Essentiale Calcium Phosphate, Tribasic)','Not Vegan',"Sodium aluminium phosphate (SAlP) describes the inorganic compounds consisting of sodium salts of aluminium phosphates. The most common SAlP has the formulas NaH14Al3(PO4)8·4H2O and Na3H15Al2(PO4)8. These materials are prepared by combining alumina, phosphoric acid, and sodium hydroxide. ",'anti-caking agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E541','Sodium aluminium phosphate (i) Acidic (ii) Basic','Vegan',"Sodium aluminium phosphate (SAlP) describes the inorganic compounds consisting of sodium salts of aluminium phosphates. The most common SAlP has the formulas NaH14Al3(PO4)8·4H2O and Na3H15Al2(PO4)8. These materials are prepared by combining alumina, phosphoric acid, and sodium hydroxide. ",'emulsifier','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E540','Dicalcium diphosphate (acidity regulator)','Vegan',"Sodium thiosulfate (sodium thiosulphate) is an inorganic compound with the formula Na2S2O3.xH2O. Typically it is available as the white or colorless pentahydrate, Na2S2O3·5H2O. The solid is an efflorescent (loses water readily) crystalline substance that dissolves well in water. It is also often called sodium hyposulfite or hypo. ",'emulsifier','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E539','Sodium thiosulfate','May or may not be vegan',"Sodium thiosulfate (sodium thiosulphate) is an inorganic compound with the formula Na2S2O3.xH2O. Typically it is available as the white or colorless pentahydrate, Na2S2O3·5H2O. The solid is an efflorescent (loses water readily) crystalline substance that dissolves well in water. It is also often called sodium hyposulfite or hypo. ",'antioxidant','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E538','Calcium ferrocyanide','Vegan',"Calcium ferrocyanide is a salt substitute, and was listed in 2012 by the EU as a \"Food Improvement Agent\". It is found at STP in the form of yellow crystals or crystalline powder. ",'anti-caking agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E537','Ferrous hexacyanomanganate','May or may not be vegan',"Potassium ferrocyanide is the inorganic compound with formula K4[Fe(CN)6]·3H2O. It is the potassium salt of the coordination complex [Fe(CN)6]4−. This salt forms lemon-yellow monoclinic crystals. ",'anti-caking agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E536','Potassium ferrocyanide','Vegan',"Potassium ferrocyanide is the inorganic compound with formula K4[Fe(CN)6]·3H2O. It is the potassium salt of the coordination complex [Fe(CN)6]4−. This salt forms lemon-yellow monoclinic crystals. ",'anti-caking agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E535','Sodium ferrocyanide (acidity regulator)','Vegan',"Magnesium oxide (MgO), or magnesia, is a white hygroscopic solid mineral that occurs naturally as periclase and is a source of magnesium (see also oxide). It has an empirical formula of MgO and consists of a lattice of Mg2+ ions and O2− ions held together by ionic bonding. Magnesium hydroxide forms in the presence of water (MgO + H2O → Mg(OH)2), but it can be reversed by heating it to separate moisture. ",'anti-caking agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E530','Magnesium oxide (acidity regulator)','Vegan',"Magnesium oxide (MgO), or magnesia, is a white hygroscopic solid mineral that occurs naturally as periclase and is a source of magnesium (see also oxide). It has an empirical formula of MgO and consists of a lattice of Mg2+ ions and O2− ions held together by ionic bonding. Magnesium hydroxide forms in the presence of water (MgO + H2O → Mg(OH)2), but it can be reversed by heating it to separate moisture. ",'anti-caking agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E529','Calcium oxide (acidity regulator)','Vegan',"Calcium oxide (CaO), commonly known as quicklime or burnt lime, is a widely used chemical compound. It is a white, caustic, alkaline, crystalline solid at room temperature. The broadly used term \"lime\" connotes calcium-containing inorganic materials, in which carbonates, oxides and hydroxides of calcium, silicon, magnesium, aluminium, and iron predominate. By contrast, quicklime specifically applies to the single chemical compound calcium oxide. Calcium oxide that survives processing without reacting in building products such as cement is called free lime. ",'improving agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E528','Magnesium hydroxide','Vegan',"Magnesium hydroxide is the inorganic compound with the chemical formula Mg(OH)2. It occurs in nature as the mineral brucite. It is a white solid with low solubility in water (Ksp = 5.61×10−12). Magnesium hydroxide is a common component of antacids, such as milk of magnesia, as well as laxatives. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E527','Ammonium hydroxide','Vegan',"Sodium hydroxide, also known as lye and caustic soda, is an inorganic compound with the formula NaOH. It is a white solid ionic compound consisting of sodium cations Na+and hydroxide anions OH−. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E526','Calcium hydroxide (acidity regulator)','Vegan',"Sodium hydroxide, also known as lye and caustic soda, is an inorganic compound with the formula NaOH. It is a white solid ionic compound consisting of sodium cations Na+and hydroxide anions OH−. ",'firming agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E525','Potassium hydroxide','Vegan',"Sodium hydroxide, also known as lye and caustic soda, is an inorganic compound with the formula NaOH. It is a white solid ionic compound consisting of sodium cations Na+and hydroxide anions OH−. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E524','Sodium hydroxide','Vegan',"Sodium hydroxide, also known as lye and caustic soda, is an inorganic compound with the formula NaOH. It is a white solid ionic compound consisting of sodium cations Na+and hydroxide anions OH−. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E523','Aluminium ammonium sulfate','Vegan',"Ammonium aluminium sulfate, also known as ammonium alum or just alum (though there are many different substances also called \"alum\"), is a white crystalline double sulfate usually encountered as the dodecahydrate, formula (NH4)Al(SO4)2·12H2O. It is used in small amounts in a variety of niche applications. The dodecahydrate occurs naturally as the rare mineral tschermigite. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E522','Aluminium potassium sulfate','Vegan',"Potassium alum, potash alum, or potassium aluminium sulfate is a chemical compound: the double sulfate of potassium and aluminium, with chemical formula KAl(SO4)2. It is commonly encountered as the dodecahydrate, KAl(SO4)2·12H2O. It crystallizes in cubic structure with space group P a -3 and lattice parameter of 12.18 Å. The compound is the most important member of the generic class of compounds called alums, and is often called simply alum. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E521','Aluminium sodium sulfate','Vegan',"Sodium aluminium sulfate is the inorganic compound with the chemical formula NaAl(SO4)2·12H2O (sometimes written Na2SO4·Al2(SO4)3·24H2O). Also known as soda alum, sodium alum, or SAS, this white solid is used in the manufacture of baking powder and as a food additive. ",'firming agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E520','Aluminium sulfate','Vegan',"Aluminium sulfate is a chemical compound with the formula Al2(SO4)3. It is soluble in water and is mainly used as a coagulating agent (promoting particle collision by neutralizing charge) in the purification of drinking water and waste water treatment plants, and also in paper manufacturing. ",'firming agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E519','Copper(II) sulfate','Vegan',"Copper(II) sulfate, also known as copper sulphate, are the inorganic compounds with the chemical formula CuSO4(H2O)x, where x can range from 0 to 5. The pentahydrate (x = 5) is the most common form. Older names for this compound include blue vitriol, bluestone, vitriol of copper, and Roman vitriol. ",'preservative','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E518','Magnesium sulfate (Epsom salts), (acidity regulator)','Vegan',"Magnesium sulfate is an inorganic salt with the formula MgSO4(H2O)x where 0≤x≤7. It is often encountered as the heptahydrate sulphate mineral epsomite (MgSO4·7H2O), commonly called Epsom salt. The overall global annual usage in the mid-1970s of the monohydrate was 2.3 million tons, of which the majority was used in agriculture. ",'firming agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E517','Ammonium sulfate','Vegan',"Ammonium sulfate (American English and international scientific usage; ammonium sulphate in British English); (NH4)2SO4, is an inorganic salt with a number of commercial uses. The most common use is as a soil fertilizer. It contains 21% nitrogen and 24% sulfur. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E516','Calcium sulfate','May or may not be vegan',"Calcium sulfate (or calcium sulphate) is the inorganic compound with the formula CaSO4 and related hydrates. In the form of γ-anhydrite (the anhydrous form), it is used as a desiccant. One particular hydrate is better known as plaster of Paris, and another occurs naturally as the mineral gypsum. It has many uses in industry. All forms are white solids that are poorly soluble in water. Calcium sulfate causes permanent hardness in water. ",'','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E515','Potassium sulfates (i) potassium sulfate (ii) potassium bisulfate','May or may not be vegan',"Potassium sulfate (in British English potassium sulphate (SOP), also called sulphate of potash, arcanite, or archaically known as potash of sulfur) is the inorganic compound with formula K2SO4. It is a white water-soluble solid. It is commonly used in fertilizers, providing both potassium and a source of sulfur. ",'','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E514','Sodium sulfates (i) Sodium sulfate (ii) sodium bisulfate','May or may not be vegan',"Sodium sulfate (also known as sodium sulphate or sulfate of soda) is the inorganic compound with formula Na2SO4 as well as several related hydrates. All forms are white solids that are highly soluble in water. With an annual production of 6 million tonnes, the decahydrate is a major commodity chemical product. It is mainly used for the manufacture of detergents and in the kraft process of paper pulping. ",'acid','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E513','Sulfuric acid','Vegan',"Sulfuric acid (alternative spelling sulphuric acid), also known as vitriol, is a mineral acid composed of the elements sulfur, oxygen and hydrogen, with molecular formula H2SO4. It is a colorless, odorless, and viscous liquid that is soluble in water and is synthesized in reactions that are highly exothermic. ",'acid','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E512','Stannous chloride','Vegan',"Tin(II) chloride, also known as stannous chloride, is a white crystalline solid with the formula SnCl2. It forms a stable dihydrate, but aqueous solutions tend to undergo hydrolysis, particularly if hot. SnCl2 is widely used as a reducing agent (in acid solution), and in electrolytic baths for tin-plating. Tin(II) chloride should not be confused with the other chloride of tin; tin(IV) chloride or stannic chloride (SnCl4). ",'antioxidant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E511','Magnesium chloride','Vegan',"Magnesium chloride is the name for the chemical compound with the formula MgCl2 and its various hydrates MgCl2(H2O)x. These salts are typical ionic halides, being highly soluble in water. The hydrated magnesium chloride can be extracted from brine or sea water. In North America, magnesium chloride is produced primarily from Great Salt Lake brine. It is extracted in a similar process from the Dead Sea in the Jordan Valley. Magnesium chloride, as the natural mineral bischofite, is also extracted (by solution mining) out of ancient seabeds, for example, the Zechstein seabed in northwest Europe. Some magnesium chloride is made from solar evaporation of seawater. Anhydrous magnesium chloride is the principal precursor to magnesium metal, which is produced on a large scale. Hydrated magnesium chloride is the form most readily available. ",'firming agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E510','Ammonium chloride, ammonia solution (acidity regulator)','Vegan',"Calcium chloride is an inorganic compound, a salt with the chemical formula CaCl2. It is a white coloured crystalline solid at room temperature, highly soluble in water. It can be created by neutralising hydrochloric acid with calcium hydroxide. ",'improving agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E509','Calcium chloride (sequestrant)','Vegan',"Calcium chloride is an inorganic compound, a salt with the chemical formula CaCl2. It is a white coloured crystalline solid at room temperature, highly soluble in water. It can be created by neutralising hydrochloric acid with calcium hydroxide. ",'firming agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E508','Potassium chloride (gelling agent)','Vegan',"Hydrochloric acid or muriatic acid is a colorless inorganic chemical system with the formula H2O:HCl. Hydrochloric acid has a distinctive pungent smell. It is classified as strongly acidic and can attack the skin over a wide composition range, since the hydrogen chloride completely dissociates in aqueous solution. ",'seasoning','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E507','Hydrochloric acid','Vegan',"Hydrochloric acid or muriatic acid is a colorless inorganic chemical system with the formula H2O:HCl. Hydrochloric acid has a distinctive pungent smell. It is classified as strongly acidic and can attack the skin over a wide composition range, since the hydrogen chloride completely dissociates in aqueous solution. ",'acid','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E505','Ferrous carbonate','May or may not be vegan',"Siderite is a mineral composed of iron(II) carbonate (FeCO3). It takes its name from the Greek word σίδηρος sideros, “iron”. It is a valuable iron mineral, since it is 48% iron and contains no sulfur or phosphorus. Zinc, magnesium and manganese commonly substitute for the iron resulting in the siderite-smithsonite, siderite-magnesite and siderite-rhodochrosite solid solution series. ",'acidity regulator','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E504','Magnesium carbonates: (i) Magnesium carbonate (ii) Magnesium bicarbonate Magnesium hydrogen carbonate','Vegan',"Magnesium carbonate, MgCO3 (archaic name magnesia alba), is an inorganic salt that is a white solid. Several hydrated and basic forms of magnesium carbonate also exist as minerals. ",'acidity regulator, anti-caking agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E503','Ammonium carbonates: (i) Ammonium carbonate (ii) Ammonium bicarbonate (Ammonium hydrogen carbonate)','Vegan',"Ammonium carbonate is a salt with the chemical formula (NH4)2CO3. Since it readily degrades to gaseous ammonia and carbon dioxide upon heating, it is used as a leavening agent and also as smelling salt. It is also known as baker's ammonia and was a predecessor to the more modern leavening agents baking soda and baking powder. It is a component of what was formerly known as sal volatile and salt of hartshorn. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E501','Potassium carbonates: (i) Potassium carbonate (ii) Potassium bicarbonate (Potassium hydrogen carbonate)','Vegan',"",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E500','Sodium carbonates: (i) Sodium carbonate (ii) Sodium bicarbonate (Sodium hydrogen carbonate) (iii) Sodium sesquicarbonate (acidity regulator)','Vegan',"",'raising agent','Approved in the EU.']);

    //Additional
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1525','Hydroxyethyl cellulose','May or may not be vegan',"Hydroxyethyl cellulose is a gelling and thickening agent derived from cellulose. It is widely used in cosmetics, cleaning solutions, and other household products. Hydroxyethyl cellulose and methyl cellulose are frequently used with hydrophobic drugs in capsule formulations, to improve the drugs' dissolution in the gastrointestinal fluids. This process is known as hydrophilization. ",'thickening agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1521','Polyethylene glycol 8000','May or may not be vegan',"Polyethylene glycol (PEG; /ˌpɒliˈɛθəlˌiːn ˈɡlaɪˌkɒl, -ˌkɔːl/) is a polyether compound with many applications, from industrial manufacturing to medicine. PEG is also known as polyethylene oxide (PEO) or polyoxyethylene (POE), depending on its molecular weight. The structure of PEG is commonly expressed as H−(O−CH2−CH2)n−OH. ",'','Approved in 2010']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1520','Propylene glycol','Vegan',"Propylene glycol (IUPAC name: propane-1,2-diol) is an organic compound with the chemical formula CH3CH(OH)CH2OH. It is a viscous, colorless liquid, which is nearly odorless but possesses a faintly sweet taste. Containing two alcohol groups, it is classed as a diol. It is miscible with a broad range of solvents, including water, acetone, and chloroform. In general, glycols are non-irritating, have very low volatility and very low toxicity. ",'humectant and flavour solvent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1519','Benzyl alcohol','May or may not be vegan',"Benzyl alcohol is an aromatic alcohol with the formula C6H5CH2OH. The benzyl group is often abbreviated \"Bn\" (not to be confused with \"Bz\" which is used for benzoyl), thus benzyl alcohol is denoted as BnOH. Benzyl alcohol is a colorless liquid with a mild pleasant aromatic odor. It is a useful solvent due to its polarity, low toxicity, and low vapor pressure. Benzyl alcohol has moderate solubility in water (4 g/100 mL) and is miscible in alcohols and diethyl ether. The anion produced by deprotonation of the alcohol group is known as benzylate or benzyloxide. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1518','Glyceryl triacetate or triacetin','Vegan',"The triglyceride 1,2,3-triacetoxypropane is more generally known as triacetin, glycerin triacetate or 1,2,3-triacetylglycerol. It is the triester of glycerol and acetylating agents, such as acetic acid and acetic anhydride. It is a colorless, viscous and odorless liquid at STP with a high boiling point and a low melting point. It has a mild, sweet taste in concentrations lower than 500 ppm, but may appear bitter at higher concentrations. ",'humectant and flavour solvent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1517','Glyceryl diacetate or diacetin','May or may not be vegan',"Glyceryl diacetate is a food additive with the E number E1517. This diglyceride is more generally known as diacetin. It is the diester of glycerol and acetylating agents, such as acetic acid and acetic anhydride. It is a colorless, viscous and odorless liquid with a high boiling point. Glycerol diaceate is typically a mixture of two isomers, 1,2-glyceryl diacetate and 1,3-glyceryl diacetate. ",'flavour solvent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1516','Glyceryl monoacetate','May or may not be vegan',"Ethanol (also called ethyl alcohol, grain alcohol, drinking alcohol, or simply alcohol) is a chemical compound, a simple alcohol with the chemical formula C2H6O. Its formula can be also written as CH3−CH2−OH or C2H5OH (an ethyl group linked to a hydroxyl group), and is often abbreviated as EtOH. Ethanol is a volatile, flammable, colorless liquid with a slight characteristic odor. It is a psychoactive substance and is the principal active ingredient found in alcoholic drinks. ",'flavour solvent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1510','Ethanol','Vegan',"Ethanol (also called ethyl alcohol, grain alcohol, drinking alcohol, or simply alcohol) is a chemical compound, a simple alcohol with the chemical formula C2H6O. Its formula can be also written as CH3−CH2−OH or C2H5OH (an ethyl group linked to a hydroxyl group), and is often abbreviated as EtOH. Ethanol is a volatile, flammable, colorless liquid with a slight characteristic odor. It is a psychoactive substance and is the principal active ingredient found in alcoholic drinks. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1505','Triethyl citrate','Vegan',"Triethyl citrate is an ester of citric acid. It is a colorless, odorless liquid used as a food additive (E number E1505) to stabilize foams, especially as whipping aid for egg white. It is also used in pharmaceutical coatings and plastics. ",'foam stabiliser','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1504','Ethyl acetate','May or may not be vegan',"Ethyl acetate (systematically ethyl ethanoate, commonly abbreviated EtOAc, ETAC or EA) is the organic compound with the formula CH3−COO−CH2−CH3, simplified to C4H8O2. This colorless liquid has a characteristic sweet smell (similar to pear drops) and is used in glues, nail polish removers, and in the decaffeination process of tea and coffee. Ethyl acetate is the ester of ethanol and acetic acid; it is manufactured on a large scale for use as a solvent. The combined annual production in 1985 of Japan, North America, and Europe was about 400,000 tonnes. In 2004, an estimated 1.3 million tonnes were produced worldwide. ",'flavour solvent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1503','Castor oil','May or may not be vegan',"Castor oil is a vegetable oil pressed from castor beans. The name probably comes from its use as a replacement for castoreum. ",'resolving agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1502','Butane-1, 3-diol','May or may not be vegan',"1,3-Butanediol is an organic compound with the formula HOCH2CH2CH(OH)CH3. It is a chiral diol. The compound is a colorless, water-soluble liquid. It has no large scale uses. It is one of four stable structural isomers of butanediol. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1501','Benzylated hydrocarbons','May or may not be vegan',"'Hydroxypropyl distarch phosphate (HDP) is a modified resistant starch. It is currently used as a food additive (INS number 1442). It is approved for use in the European Union (listed as E1442), the United States, Australia, Taiwan, and New Zealand. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1452','Starch aluminium octenyl succinate','May or may not be vegan',"'Hydroxypropyl distarch phosphate (HDP) is a modified resistant starch. It is currently used as a food additive (INS number 1442). It is approved for use in the European Union (listed as E1442), the United States, Australia, Taiwan, and New Zealand. ",'','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1451','Acetylated oxidised starch (emulsifier)','Vegan',"'Hydroxypropyl distarch phosphate (HDP) is a modified resistant starch. It is currently used as a food additive (INS number 1442). It is approved for use in the European Union (listed as E1442), the United States, Australia, Taiwan, and New Zealand. ",'thickening agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1450','Starch sodium octenyl succinate (emulsifier) (stabiliser)','Vegan',"'Hydroxypropyl distarch phosphate (HDP) is a modified resistant starch. It is currently used as a food additive (INS number 1442). It is approved for use in the European Union (listed as E1442), the United States, Australia, Taiwan, and New Zealand. ",'thickening agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1443','Hydroxy propyl distarch glycerol','May or may not be vegan',"'Hydroxypropyl distarch phosphate (HDP) is a modified resistant starch. It is currently used as a food additive (INS number 1442). It is approved for use in the European Union (listed as E1442), the United States, Australia, Taiwan, and New Zealand. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1442','Hydroxy propyl distarch phosphate (stabiliser)','Vegan',"'Hydroxypropyl distarch phosphate (HDP) is a modified resistant starch. It is currently used as a food additive (INS number 1442). It is approved for use in the European Union (listed as E1442), the United States, Australia, Taiwan, and New Zealand. ",'thickening agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1441','Hydroxy propyl distarch glycerine (stabiliser)','Vegan',"Hydroxypropyl starch is a type of modified starch used as a food additive. I has the E number E1440. Hydroxyl propyl starch is not absorbed intact by the gut, but are significantly hydrolyzed by intestinal enzymes and then fermented by intestinal microbiota. ",'thickening agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1440','Hydroxy propyl starch (emulsifier)','Vegan',"Hydroxypropyl starch is a type of modified starch used as a food additive. I has the E number E1440. Hydroxyl propyl starch is not absorbed intact by the gut, but are significantly hydrolyzed by intestinal enzymes and then fermented by intestinal microbiota. ",'thickening agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1430','Distarch glycerine (stabiliser)','Vegan',"Acetylated distarch adipate (E1422), is a starch that is treated with acetic anhydride and adipic acid anhydride to resist high temperatures. It is used in foods as a bulking agent, stabilizer and a thickener. ",'thickening agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1423','Acetylated distarch glycerol','May or may not be vegan',"Acetylated distarch adipate (E1422), is a starch that is treated with acetic anhydride and adipic acid anhydride to resist high temperatures. It is used in foods as a bulking agent, stabilizer and a thickener. ",'thickening agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1422','Acetylated distarch adipate (stabiliser)','Vegan',"Acetylated distarch adipate (E1422), is a starch that is treated with acetic anhydride and adipic acid anhydride to resist high temperatures. It is used in foods as a bulking agent, stabilizer and a thickener. ",'thickening agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1421','Starch acetate esterified with vinyl acetate (stabiliser)','Vegan',"Acetylated starch, E1420 in the E number scheme of food additives, is a modified starch. These are not absorbed intact by the gut, but are significantly hydrolysed by intestinal enzymes and then fermented by intestinal microbiota. ",'thickening agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1420','Starch acetate esterified with acetic anhydride (stabiliser)','Vegan',"Acetylated starch, E1420 in the E number scheme of food additives, is a modified starch. These are not absorbed intact by the gut, but are significantly hydrolysed by intestinal enzymes and then fermented by intestinal microbiota. ",'thickening agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1414','Acetylated distarch phosphate (emulsifier)','Vegan',"Acetylated distarch phosphate, E1414 in the E number scheme of food additives, is a modified starch. These are not absorbed intact by the gut, but are significantly hydrolysed by intestinal enzymes and then fermented by intestinal microbiota. ",'thickening agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1413','Phosphated distarch phosphate (stabiliser)','Vegan',"Phosphated distarch phosphate, is a modified resistant starch. It is derived from high amylose maize starch and contains a minimum of 70% dietary fibre. It is currently used as a food additive (E1413) as a freeze-thaw-stable thickener (stabilises the consistency of the foodstuff when frozen and thawed) within the European Union in products such as soups, sauces, frozen gravies and pie fillings. ",'thickening agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1412','Distarch phosphate esterified with sodium trimetasphosphate; esterified with phosphorus oxychloride (stabiliser)','Vegan',"Monostarch phosphate, E1410 in the E number scheme of food additives, is a modified starches. These are not absorbed intact by the gut, but are significantly hydrolysed by intestinal enzymes and then fermented by intestinal microbiota. ",'thickening agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1411','Distarch glycerol (thickening agent)','May or may not be vegan',"Monostarch phosphate, E1410 in the E number scheme of food additives, is a modified starches. These are not absorbed intact by the gut, but are significantly hydrolysed by intestinal enzymes and then fermented by intestinal microbiota. ",'emulsifier','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1410','Monostarch phosphate (stabiliser)','Vegan',"Monostarch phosphate, E1410 in the E number scheme of food additives, is a modified starches. These are not absorbed intact by the gut, but are significantly hydrolysed by intestinal enzymes and then fermented by intestinal microbiota. ",'thickening agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1405','Enzyme treated starch','May or may not be vegan',"Modified starch, also called starch derivatives, are prepared by physically, enzymatically, or chemically treating native starch to change its properties. Modified starches are used in practically all starch applications, such as in food products as a thickening agent, stabilizer or emulsifier; in pharmaceuticals as a disintegrant; or as binder in coated paper. They are also used in many other applications. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1404','Oxidized starch (emulsifier)','Vegan',"Modified starch, also called starch derivatives, are prepared by physically, enzymatically, or chemically treating native starch to change its properties. Modified starches are used in practically all starch applications, such as in food products as a thickening agent, stabilizer or emulsifier; in pharmaceuticals as a disintegrant; or as binder in coated paper. They are also used in many other applications. ",'thickening agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1403','Bleached starch (stabiliser)','Vegan',"Modified starch, also called starch derivatives, are prepared by physically, enzymatically, or chemically treating native starch to change its properties. Modified starches are used in practically all starch applications, such as in food products as a thickening agent, stabilizer or emulsifier; in pharmaceuticals as a disintegrant; or as binder in coated paper. They are also used in many other applications. ",'thickening agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1402','Alkaline modified starch (stabiliser)','Vegan',"Modified starch, also called starch derivatives, are prepared by physically, enzymatically, or chemically treating native starch to change its properties. Modified starches are used in practically all starch applications, such as in food products as a thickening agent, stabilizer or emulsifier; in pharmaceuticals as a disintegrant; or as binder in coated paper. They are also used in many other applications. ",'thickening agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1401','Modified starch ((Acid-treated starch) stabiliser)','Vegan',"Modified starch, also called starch derivatives, are prepared by physically, enzymatically, or chemically treating native starch to change its properties. Modified starches are used in practically all starch applications, such as in food products as a thickening agent, stabilizer or emulsifier; in pharmaceuticals as a disintegrant; or as binder in coated paper. They are also used in many other applications. ",'thickening agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1400','Dextrin (Dextrins, roasted starch white and yellow) (stabiliser)','Vegan',"Dextrins are a group of low-molecular-weight carbohydrates produced by the hydrolysis of starch or glycogen. Dextrins are mixtures of polymers of D-glucose units linked by α-(1→4) or α-(1→6) glycosidic bonds. ",'thickening agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1204','Pullulan','May or may not be vegan',"Pullulan is a polysaccharide polymer consisting of maltotriose units, also known as α-1,4- ;α-1,6-glucan'. Three glucose units in maltotriose are connected by an α-1,4 glycosidic bond, whereas consecutive maltotriose units are connected to each other by an α-1,6 glycosidic bond. Pullulan is produced from starch by the fungus Aureobasidium pullulans. Pullulan is mainly used by the cell to resist desiccation and predation. The presence of this polysaccharide also facilitates diffusion of molecules both into and out of the cell. ",'','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1203','Polyvinyl alcohol','May or may not be vegan',"Poly(vinyl alcohol) (PVOH, PVA, or PVAl) is a water-soluble synthetic polymer. It has the idealized formula [CH2CH(OH)]n. It is used in papermaking, textiles, and a variety of coatings. It is colourless (white) and odorless. It is sometimes supplied as beads or as solutions in water. ",'','Approved in 2010']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1202','Polyvinylpolypyrrolidone (carrier)','Vegan',"Polyvinylpolypyrrolidone (polyvinyl polypyrrolidone, PVPP, crospovidone, crospolividone or E1202) is a highly cross-linked modification of polyvinylpyrrolidone (PVP). ",'stabiliser','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1201','Polyvinylpyrrolidone','Vegan',"Polyvinylpyrrolidone (PVP), also commonly called polyvidone or povidone, is a water-soluble polymer made from the monomer N-vinylpyrrolidone. ",'stabiliser','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1200','Polydextrose','Vegan',"Polydextrose is a synthetic polymer of glucose. It is a food ingredient classified as soluble fiber by the U.S. Food and Drug Administration (FDA) as well as Health Canada, as of April 2013[update]. It is frequently used to increase the dietary fiber content of food, to replace sugar, and to reduce calories and fat content. It is a multi-purpose food ingredient synthesized from dextrose (glucose), plus about 10 percent sorbitol and 1 percent citric acid. Its E number is E1200. The FDA approved it in 1981. ",'stabiliser, thickening agent, humectant, carrier','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1105','Lysozyme','Vegan',"Lysozyme, also known as muramidase or N-acetylmuramide glycanhydrolase, is an antimicrobial enzyme produced by animals that forms part of the innate immune system. Lysozyme is a glycoside hydrolase that catalyzes the hydrolysis of 1,4-beta-linkages between N-acetylmuramic acid and N-acetyl-D-glucosamine residues in peptidoglycan, which is the major component of gram-positive bacterial cell wall. This hydrolysis in turn compromises the integrity of bacterial cell walls causing lysis of the bacteria. ",'preservative','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1104','Lipases','May or may not be vegan',"A lipase (/ˈlaɪpeɪs/, /-peɪz/) is any enzyme that catalyzes the hydrolysis of fats (lipids). Lipases are a subclass of the esterases. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1103','Invertase','Vegan',"Sucrase is a digestive enzyme that catalyzes the hydrolysis of sucrose to its subunits fructose and glucose. One form, sucrase-isomaltase, is secreted in the small intestine on the brush border. The sucrase enzyme invertase, which occurs more commonly in plants, also hydrolyzes sucrose but by a different mechanism. ",'stabiliser','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1102','Glucose oxidase','May or may not be vegan',"The glucose oxidase enzyme (GOx or GOD) also known as notatin (EC number 1.1.3.4) is an oxido-reductase that catalyses the oxidation of glucose to hydrogen peroxide and D-glucono-δ-lactone. This enzyme is produced by certain species of fungi and insects and displays antibacterial activity when oxygen and glucose are present. ",'antioxidant','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1101','Proteases ((i)Protease, (ii)Papain, (iii)Bromelain, (iv)Ficin)','May or may not be vegan',"A protease (also called a peptidase or proteinase) is an enzyme that catalyzes (increases the rate of) proteolysis, the breakdown of proteins into smaller polypeptides or single amino acids. They do this by cleaving the peptide bonds within proteins by hydrolysis, a reaction where water breaks bonds. Proteases are involved in many biological functions, including digestion of eaten/swallowed proteins, protein catabolism (breakdown of old proteins), and cell signalling. ",'stabiliser, flavour enhancer','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1100','Amylase','May or may not be vegan',"Amylase (/ˈæmɪleɪz/) is an enzyme that catalyses the hydrolysis of starch (Latin amylum) into sugars. Amylase is present in the saliva of humans and some other mammals, where it begins the chemical process of digestion. Foods that contain large amounts of starch but little sugar, such as rice and potatoes, may acquire a slightly sweet taste as they are chewed because amylase degrades some of their starch into sugar. The pancreas and salivary gland make amylase (alpha amylase) to hydrolyse dietary starch into disaccharides and trisaccharides which are converted by other enzymes to glucose to supply the body with energy. Plants and some bacteria also produce amylase. As diastase, amylase was the first enzyme to be discovered and isolated (by Anselme Payen in 1833). Specific amylase proteins are designated by different Greek letters. All amylases are glycoside hydrolases and act on α-1,4-glycosidic bonds. ",'stabiliser, flavour enhancer','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1001','Choline salts','May or may not be vegan',"Choline /ˈkəʊliːn/ occurs as a cation that forms various salts (X− in the depicted formula is an undefined counteranion). Choline is an essential nutrient for humans and many other animals. To maintain health, it must be obtained from the diet as choline or as choline phospholipids, like phosphatidylcholine. Humans and most animals make choline de novo, but production is insufficient in humans and most species. Choline is often not classified as a vitamin, but as a nutrient with an amino acid-like metabolism. In most animals, choline phospholipids are necessary components in cell membranes, in the membranes of cell organelles, and in very low-density lipoproteins. Choline is required to produce acetylcholine – a neurotransmitter – and S-adenosyl methionine, a universal methyl donor involved in the synthesis of homocysteine. ",'emulsifier','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E1000','Cholic acid','May or may not be vegan',"",'emulsifier','']);

    //Antibiotics
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E717','Avilamycin','May or may not be vegan',"Salinomycin is an antibacterial and coccidiostat ionophore therapeutic drug. ",'antibiotic','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E716','Salinomycin','May or may not be vegan',"Salinomycin is an antibacterial and coccidiostat ionophore therapeutic drug. ",'antibiotic','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E715','Avoparcin','May or may not be vegan',"Avoparcin is a glycopeptide antibiotic effective against Gram-positive bacteria. It has been used in agriculture as an additive to livestock feed to promote growth in chickens, pigs, and cattle. It is also used as an aid in the prevention of necrotic enteritis in poultry. ",'antibiotic','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E714','Monensin A','May or may not be vegan',"Monensin is a polyether antibiotic isolated from Streptomyces cinnamonensis. It is widely used in ruminant animal feeds. ",'antibiotic','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E713','Tylosin','May or may not be vegan',"Tylosin is an antibiotic and a bacteriostatic feed additive used in veterinary medicine. It has a broad spectrum of activity against Gram-positive organisms and a limited range of Gram-negative organisms. It is found naturally as a fermentation product of Streptomyces fradiae. It is a macrolide antibiotic. ",'antibiotic','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E712','Flavomycin','May or may not be vegan',"Spiramycin is a macrolide antibiotic and antiparasitic. It is used to treat toxoplasmosis and various other infections of soft tissues. Although used in Europe, Canada and Mexico, spiramycin is still considered an experimental drug in the United States, but can sometimes be obtained by special permission from the FDA for toxoplasmosis in the first trimester of pregnancy. Another treatment option (typically used after 16w gestation) are a combination of pyrimethamine and sulfadiazine (given with leucovorin).  ",'antibiotic','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E711','Virginiamycins','May or may not be vegan',"Spiramycin is a macrolide antibiotic and antiparasitic. It is used to treat toxoplasmosis and various other infections of soft tissues. Although used in Europe, Canada and Mexico, spiramycin is still considered an experimental drug in the United States, but can sometimes be obtained by special permission from the FDA for toxoplasmosis in the first trimester of pregnancy. Another treatment option (typically used after 16w gestation) are a combination of pyrimethamine and sulfadiazine (given with leucovorin).  ",'antibiotic','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E710','Spiramycins','May or may not be vegan',"Spiramycin is a macrolide antibiotic and antiparasitic. It is used to treat toxoplasmosis and various other infections of soft tissues. Although used in Europe, Canada and Mexico, spiramycin is still considered an experimental drug in the United States, but can sometimes be obtained by special permission from the FDA for toxoplasmosis in the first trimester of pregnancy. Another treatment option (typically used after 16w gestation) are a combination of pyrimethamine and sulfadiazine (given with leucovorin).  ",'antibiotic','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E708','Penicillin G benzathine','May or may not be vegan',"Oleandomycin is a macrolide antibiotic. It is synthesized from strains of Streptomyces antibioticus. It is weaker than erythromycin. ",'antibiotic','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E707','Penicillin G procaine','May or may not be vegan',"Oleandomycin is a macrolide antibiotic. It is synthesized from strains of Streptomyces antibioticus. It is weaker than erythromycin. ",'antibiotic','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E706','Penicillin G sodium','May or may not be vegan',"Oleandomycin is a macrolide antibiotic. It is synthesized from strains of Streptomyces antibioticus. It is weaker than erythromycin. ",'antibiotic','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E705','Penicillin G potassium','May or may not be vegan',"Oleandomycin is a macrolide antibiotic. It is synthesized from strains of Streptomyces antibioticus. It is weaker than erythromycin. ",'antibiotic','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E704','Oleandomycin','May or may not be vegan',"Oleandomycin is a macrolide antibiotic. It is synthesized from strains of Streptomyces antibioticus. It is weaker than erythromycin. ",'antibiotic','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E703','Oxytetracycline','May or may not be vegan',"Oxytetracycline was the second of the broad-spectrum tetracycline group of antibiotics to be discovered. ",'antibiotic','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E702','Chlortetracycline','May or may not be vegan',"Chlortetracycline (trade name Aureomycin, Lederle Laboratories) is a tetracycline antibiotic, the first tetracycline to be identified. It was discovered in 1945 at Lederle Laboratories under the supervision of scientist Yellapragada Subbarow and his junior Benjamin Minge Duggar. Duggar identified the antibiotic as the product of an actinomycete he cultured from a soil sample collected from Sanborn Field at the University of Missouri. The organism was named Streptomyces aureofaciens and the isolated drug, Aureomycin, because of their golden color. ",'antibiotic','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E701','Tetracyclines','May or may not be vegan',"Tetracyclines are a group of broad-spectrum antibiotic compounds that have a common basic structure and are either isolated directly from several species of Streptomyces bacteria or produced semi-synthetically from those isolated compounds. Tetracycline molecules comprise a linear fused tetracyclic nucleus (rings designated A, B, C and D) to which a variety of functional groups are attached. Tetracyclines are named for their four (\"tetra-\") hydrocarbon rings (\"-cycl-\") derivation (\"-ine\"). They are defined as a subclass of polyketides, having an octahydrotetracene-2-carboxamide skeleton and are known as derivatives of polycyclic naphthacene carboxamide. While all tetracyclines have a common structure, they differ from each other by the presence of chloride, methyl, and hydroxyl groups. These modifications do not change their broad antibacterial activity, but do affect pharmacological properties such as half-life and binding to proteins in serum. ",'antibiotic','']);

    //Antioxidants
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E399','Calcium lactobionate','May or may not be vegan',"Lactobionic acid (4-O-β-galactopyranosyl-D-gluconic acid) is a sugar acid. It is a disaccharide formed from gluconic acid and galactose. It can be formed by oxidation of lactose. The carboxylate anion of lactobionic acid is known as lactobionate. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E392','Extracts of rosemary','May or may not be vegan',"Salvia rosmarinus, commonly known as rosemary, is a woody, perennial herb with fragrant, evergreen, needle-like leaves and white, pink, purple, or blue flowers, native to the Mediterranean region. Until 2017, it was known by the scientific name Rosmarinus officinalis, now a synonym. ",'','Approved in 2010']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E391','Phytic acid','May or may not be vegan',"Phytic acid is a six-fold dihydrogenphosphate ester of inositol (specifically, of the myo isomer), also called inositol hexakisphosphate (IP6) or inositol polyphosphate. At physiological pH, the phosphates are partially ionized, resulting in the phytate anion. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E390','Distearyl thiodipropionate','May or may not be vegan',"A sequestrant is a food additive which improves the quality and stability of foods. A sequestrant forms chelate complexes with polyvalent metal ions, especially copper, iron and nickel, which can prevent the oxidation of the fats in the food. Sequestrants are a type of preservative. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E389','Dilauryl thiodipropionate','May or may not be vegan',"A sequestrant is a food additive which improves the quality and stability of foods. A sequestrant forms chelate complexes with polyvalent metal ions, especially copper, iron and nickel, which can prevent the oxidation of the fats in the food. Sequestrants are a type of preservative. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E388','Thiodipropionic acid','May or may not be vegan',"A sequestrant is a food additive which improves the quality and stability of foods. A sequestrant forms chelate complexes with polyvalent metal ions, especially copper, iron and nickel, which can prevent the oxidation of the fats in the food. Sequestrants are a type of preservative. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E387','Oxystearin','May or may not be vegan',"A sequestrant is a food additive which improves the quality and stability of foods. A sequestrant forms chelate complexes with polyvalent metal ions, especially copper, iron and nickel, which can prevent the oxidation of the fats in the food. Sequestrants are a type of preservative. ",'stabiliser','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E386','Disodium ethylene diamine tetraacetate (Disodium EDTA)','May or may not be vegan',"A sequestrant is a food additive which improves the quality and stability of foods. A sequestrant forms chelate complexes with polyvalent metal ions, especially copper, iron and nickel, which can prevent the oxidation of the fats in the food. Sequestrants are a type of preservative. ",'sequestrant','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E385','Calcium disodium ethylene diamine tetraacetate, (Calcium disodium EDTA)','Vegan',"Ethylenediaminetetraacetic acid (EDTA), also known by several other names, is a chemical used for both industrial and medical purposes. It was synthesized for the first time in 1935 by Ferdinand Münz. ",'sequestrant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E384','Isopropyl citrate','May or may not be vegan',"The E series is a system of preferred numbers (also called preferred values) derived for use in electronic components. It consists of the E3, E6, E12, E24, E48, E96 and E192 series, where the number after the 'E' designates the quantity of value \"steps\" in each series. Although it is theoretically possible to produce components of any value, in practice the need for inventory simplification has led the industry to settle on the E series for resistors, capacitors, inductors, and zener diodes. Other types of electrical components are either specified by the Renard series (for example fuses) or are defined in relevant product standards (for example IEC 60228 for wires). ",'acidity regulator','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E383','Calcium glycerylphosphate','May or may not be vegan',"Calcium glycerylphosphate (or calcium glycerophosphate) is a mineral supplement. ",'acidity regulator','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E381','Ammonium ferric citrate','Vegan',"Ammonium ferric citrate has the formula (NH4)5[Fe(C6H4O7)2]. A distinguishing feature of this compound is that it is very soluble in water, in contrast to ferric citrate which is not very soluble. ",'acidity regulator','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E380','Triammonium citrate','Vegan',"Ammonium fumarate is a compound with formula (NH4)2(C2H2(COO)2). It is the ammonium salt of fumaric acid. As a food additive, it has the E number E368. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E375','Niacin','Vegan',"Ammonium fumarate is a compound with formula (NH4)2(C2H2(COO)2). It is the ammonium salt of fumaric acid. As a food additive, it has the E number E368. ",'acidity regulator','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E370','1,4-Heptonolactone','Vegan',"Ammonium fumarate is a compound with formula (NH4)2(C2H2(COO)2). It is the ammonium salt of fumaric acid. As a food additive, it has the E number E368. ",'acidity regulator','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E368','Ammonium fumarate','May or may not be vegan',"Ammonium fumarate is a compound with formula (NH4)2(C2H2(COO)2). It is the ammonium salt of fumaric acid. As a food additive, it has the E number E368. ",'acidity regulator','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E367','Calcium fumarate','Vegan',"Fumaric acid or trans-butenedioic acid is a chemical compound with the formula HO2CCH=CHCO2H. Fumaric acid has a fruit-like taste and has been used as a food acidulant since 1946. Its E number is E297. ",'acidity regulator','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E366','Potassium fumarate','Vegan',"Potassium fumarate is a compound with formula K2C4H2O4. It is the potassium salt of fumaric acid. ",'acidity regulator','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E365','Monosodium fumarate','Vegan',"Succinic acid (/səkˈsɪnɪk/) is a dicarboxylic acid with the chemical formula (CH2)2(CO2H)2. The name derives from Latin succinum, meaning amber. In living organisms, succinic acid takes the form of an anion, succinate, which has multiple biological roles as a metabolic intermediate being converted into fumarate by the enzyme succinate dehydrogenase in complex 2 of the electron transport chain which is involved in making ATP, and as a signaling molecule reflecting the cellular metabolic state. It is marketed as food additive E363. Succinate is generated in mitochondria via the tricarboxylic acid cycle (TCA). Succinate can exit the mitochondrial matrix and function in the cytoplasm as well as the extracellular space, changing gene expression patterns, modulating epigenetic landscape or demonstrating hormone-like signaling. As such, succinate links cellular metabolism, especially ATP formation, to the regulation of cellular function. Dysregulation of succinate synthesis, and therefore ATP synthesis, happens in some genetic mitochondrial diseases, such as Leigh syndrome, and Melas syndrome, and degradation can lead to pathological conditions, such as malignant transformation, inflammation and tissue injury. ",'acidity regulator','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E363','Succinic acid','Vegan',"Succinic acid (/səkˈsɪnɪk/) is a dicarboxylic acid with the chemical formula (CH2)2(CO2H)2. The name derives from Latin succinum, meaning amber. In living organisms, succinic acid takes the form of an anion, succinate, which has multiple biological roles as a metabolic intermediate being converted into fumarate by the enzyme succinate dehydrogenase in complex 2 of the electron transport chain which is involved in making ATP, and as a signaling molecule reflecting the cellular metabolic state. It is marketed as food additive E363. Succinate is generated in mitochondria via the tricarboxylic acid cycle (TCA). Succinate can exit the mitochondrial matrix and function in the cytoplasm as well as the extracellular space, changing gene expression patterns, modulating epigenetic landscape or demonstrating hormone-like signaling. As such, succinate links cellular metabolism, especially ATP formation, to the regulation of cellular function. Dysregulation of succinate synthesis, and therefore ATP synthesis, happens in some genetic mitochondrial diseases, such as Leigh syndrome, and Melas syndrome, and degradation can lead to pathological conditions, such as malignant transformation, inflammation and tissue injury. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E359','Ammonium adipate','May or may not be vegan',"Ammonium adipate is a compound with formula (NH4)2(C4H8(COO)2). It is the ammonium salt of adipic acid. It is used as a food additive and has the E number E359. ",'acidity regulator','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E357','Potassium adipate','Vegan',"Potassium adipate is a compound with formula K2C6H8O4. It is the potassium salt of adipic acid. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E356','Sodium adipate','Vegan',"Sodium adipate is a compound with formula Na2C6H8O4. It is the sodium salt of adipic acid. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E355','Adipic acid','Vegan',"Calcium tartrate, exactly calcium L-tartrate, is a byproduct of the wine industry, prepared from wine fermentation dregs. It is the calcium salt of L-tartaric acid, an acid most commonly found in grapes. Its solubility decreases with lower temperature, which results in the forming of whitish (in red wine often reddish) crystalline clusters as it precipitates. As E number E354, it finds use as a food preservative and acidity regulator. Like tartaric acid, calcium tartrate has two asymmetric carbons, hence it has two chiral isomers and a non-chiral isomer (meso-form). Most calcium tartrate of biological origin is the chiral levorotatory (–) isomer. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E354','Calcium tartrate','Vegan',"Calcium tartrate, exactly calcium L-tartrate, is a byproduct of the wine industry, prepared from wine fermentation dregs. It is the calcium salt of L-tartaric acid, an acid most commonly found in grapes. Its solubility decreases with lower temperature, which results in the forming of whitish (in red wine often reddish) crystalline clusters as it precipitates. As E number E354, it finds use as a food preservative and acidity regulator. Like tartaric acid, calcium tartrate has two asymmetric carbons, hence it has two chiral isomers and a non-chiral isomer (meso-form). Most calcium tartrate of biological origin is the chiral levorotatory (–) isomer. ",'emulsifier','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E353','Metatartaric acid','Vegan',"Calcium malate is a compound with formula Ca(C2H4O(COO)2). It is the calcium salt of malic acid. As a food additive, it has the E number E352. ",'emulsifier','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E352','Calcium malates (i) Calcium malate (ii) Calcium hydrogen malate','Vegan',"Calcium malate is a compound with formula Ca(C2H4O(COO)2). It is the calcium salt of malic acid. As a food additive, it has the E number E352. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E351','Potassium malate','Vegan',"The E351 series (E351系) was a tilting electric multiple unit (EMU) train type operated by East Japan Railway Company (JR East) on Chuo Main Line Super Azusa limited express services in Japan from 1993 to 2018. First introduced in December 1993 (initially on Azusa services), a total of 60 vehicles were built, formed as five eight-car main sets (numbers S1 to S5) and five four-car supplementary (numbers S21 to S25) sets. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E350','Sodium malates (i) Sodium malate (ii) Sodium hydrogen malate','Vegan',"Ammonium malate is a compound with formula NH4(C2H4O(COO)2). It is the ammonium salt of malic acid. It is used as a food additive and has the E number E349. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E349','Ammonium malate','May or may not be vegan',"Ammonium malate is a compound with formula NH4(C2H4O(COO)2). It is the ammonium salt of malic acid. It is used as a food additive and has the E number E349. ",'acidity regulator','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E345','Magnesium citrate','May or may not be vegan',"Magnesium citrate is a magnesium preparation in salt form with citric acid in a 1:1 ratio (1 magnesium atom per citrate molecule). The name \"magnesium citrate\" is ambiguous and sometimes may refer to other salts such as trimagnesium citrate which has a magnesium:citrate ratio of 3:2. ",'acidity regulator','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E344','Lecithin citrate','May or may not be vegan',"Lecithin (UK: /ˈlɛsɪθɪn/, US: /ˈlɛsəθɪn/, from the Greek lekithos \"yolk\") is a generic term to designate any group of yellow-brownish fatty substances occurring in animal and plant tissues which are amphiphilic – they attract both water and fatty substances (and so are both hydrophilic and lipophilic), and are used for smoothing food textures, emulsifying, homogenizing liquid mixtures, and repelling sticking materials. ",'acidity regulator','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E343','Magnesium phosphates (i) monomagnesium phosphate (ii) Dimagnesium phosphate','Vegan',"Magnesium phosphate is a general term for salts of magnesium and phosphate appearing in several forms and several hydrates: ",'anti-caking agent','Approved in the EU. This additive is under discussion and may be included in a future amendment to the Directive on miscellaneous additives.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E342','Ammonium phosphates: (i) monoammonium phosphate (ii) diammonium phosphate','May or may not be vegan',"Ammonium phosphate is the salt of ammonium and phosphate. It is a highly unstable compound with the formula (NH4)3PO4. Because of its instability, it is elusive and of no commercial value. A related \"double salt\", (NH4)3PO4.(NH4)2HPO4 is also recognized but is too unstable for practical use. Both triammonium salts evolve ammonia. In contrast to the fragile nature of the triammonium salts, the diammonium phosphate (NH4)2HPO4 monoammonium salt (NH4)H2PO4, are stable materials that are commonly used as fertilizers to provide plants with fixed nitrogen and phosphorus. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E341','Calcium phosphates (i) Monocalcium phosphate (ii) Dicalcium phosphate (iii) Tricalcium phosphate','Vegan',"Calcium phosphate is a family of materials and minerals containing calcium ions (Ca2+) together with inorganic phosphate anions. Some so-called calcium phosphates contain oxide and hydroxide as well. They are white solids of nutritious value. Calcium phosphates are found in many living organisms, e.g., bone mineral and tooth enamel. In milk, it exists in a colloidal form in micelles bound to casein protein with magnesium, zinc, and citrate - collectively referred to as colloidal calcium phosphate (CCP). Various calcium phosphate minerals are used in the production of phosphoric acid and fertilizers. Overuse of certain forms of calcium phosphate can lead to nutrient-containing surface runoff and subsequent adverse effects upon receiving waters such as algal blooms and eutrophication. ",'anti-caking agent, firming agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E340','Potassium phosphates (i) Monopotassium phosphate (ii) Dipotassium phosphate (iii) Tripotassium phosphate','Vegan',"Potassium phosphate is a generic term for the salts of potassium and phosphate ions including: ",'antioxidant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E339','Sodium phosphates (i) Monosodium phosphate (ii) Disodium phosphate (iii) Trisodium phosphate','Vegan',"Trisodium phosphate (TSP) is the inorganic compound with the chemical formula Na3PO4. It is a white, granular or crystalline solid, highly soluble in water, producing an alkaline solution. TSP is used as a cleaning agent, builder, lubricant, food additive, stain remover, and degreaser. ",'antioxidant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E338','Orthophosphoric acid','Vegan',"Phosphoric acid (also known as orthophosphoric acid or phosphoric(V) acid) is a weak acid with the chemical formula H3PO4. Orthophosphoric acid refers to phosphoric acid, which is the IUPAC name for this compound. The prefix ortho- is used to distinguish the acid from related phosphoric acids, called polyphosphoric acids. Orthophosphoric acid is a non-toxic acid, which, when pure, is a solid at room temperature and pressure. The conjugate base of phosphoric acid is the dihydrogen phosphate ion, H2PO−4, which in turn has a conjugate base of hydrogen phosphate, HPO2−4, which has a conjugate base of phosphate, PO3−4. Phosphates are essential for life, being building blocks for both DNA and RNA. ",'acid','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E337','Sodium potassium tartrate','Vegan',"Potassium sodium tartrate tetrahydrate, also known as Rochelle salt, is a double salt of tartaric acid first prepared (in about 1675) by an apothecary, Pierre Seignette, of La Rochelle, France. Potassium sodium tartrate and monopotassium phosphate were the first materials discovered to exhibit piezoelectricity. This property led to its extensive use in \"crystal\" gramophone (phono) pick-ups, microphones and earpieces during the post-World War II consumer electronics boom of the mid-20th Century. Such transducers had an exceptionally high output with typical pick-up cartridge outputs as much as 2 volts or more. Rochelle salt is deliquescent so any transducers based on the material deteriorated if stored in damp conditions. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E336','Potassium tartrates (i) Monopotassium tartrate (cream of tartar) (ii) Dipotassium tartrate','Vegan',"Potassium tartrate, dipotassium tartrate or argol has formula K2C4H4O6. It is the potassium salt of tartaric acid. It is often confused with potassium bitartrate, also known as cream of tartar. As a food additive, it shares the E number E336 with potassium bitartrate. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E335','Sodium tartrates (i) Monosodium tartrate (ii), Disodium tartrate','Vegan',"Sodium tartrate (Na2C4H4O6) is used as an emulsifier and a binding agent in food products such as jellies, margarine, and sausage casings. As a food additive, it is known by the E number E335. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E334','Tartaric acid (L(+)-)','Vegan',"Tartaric acid is a white, crystalline organic acid that occurs naturally in many fruits, most notably in grapes, but also in bananas, tamarinds, and citrus. Its salt, potassium bitartrate, commonly known as cream of tartar, develops naturally in the process of winemaking. It is commonly mixed with sodium bicarbonate and is sold as baking powder used as a leavening agent in food preparation. The acid itself is added to foods as an antioxidant E334 and to impart its distinctive sour taste. ",'(acid)','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E333','Calcium citrates (i) Monocalcium citrate (ii) Dicalcium citrate (iii) Calcium citrate (tricalcium citrate)','Vegan',"Calcium citrate is the calcium salt of citric acid. It is commonly used as a food additive (E333), usually as a preservative, but sometimes for flavor. In this sense, it is similar to sodium citrate. Calcium citrate is also found in some dietary calcium supplements (e.g. Citracal). Calcium makes up 24.1% of calcium citrate (anhydrous) and 21.1% of calcium citrate (tetrahydrate) by mass. The tetrahydrate occurs in nature as the mineral Earlandite. ",'acidity regulator, firming agent, sequestrant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E332','Potassium citrates (i) Monopotassium citrate (ii) Potassium citrate (tripotassium citrate)','Vegan',"Potassium citrate (also known as tripotassium citrate) is a potassium salt of citric acid with the molecular formula K3C6H5O7. It is a white, hygroscopic crystalline powder. It is odorless with a saline taste. It contains 38.28% potassium by mass. In the monohydrate form it is highly hygroscopic and deliquescent. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E331','Sodium citrates (i) Monosodium citrate (ii) Disodium citrate (iii) Sodium citrate (trisodium citrate)','Vegan',"Citric acid is a weak organic acid that has the chemical formula C6H8O7. It occurs naturally in citrus fruits. In biochemistry, it is an intermediate in the citric acid cycle, which occurs in the metabolism of all aerobic organisms. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E330','Citric acid','Vegan',"Citric acid is a weak organic acid that has the chemical formula C6H8O7. It occurs naturally in citrus fruits. In biochemistry, it is an intermediate in the citric acid cycle, which occurs in the metabolism of all aerobic organisms. ",'acid, acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E329','Magnesium lactate','Vegan',"Magnesium lactate, the magnesium salt of lactic acid, is a mineral supplement. ",'acidity regulator','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E328','Ammonium lactate','May or may not be vegan',"Ammonium lactate is a compound with formula NH4(C2H4(OH)COO). It is the ammonium salt of lactic acid. It has mild anti-bacterial properties. ",'acidity regulator','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E327','Calcium lactate','May or may not be vegan',"Calcium lactate is a white crystalline salt with formula C6H10CaO6, consisting of two lactate anions H3C(CHOH)CO−2 for each calcium cation Ca2+. It forms several hydrates, the most common being the pentahydrate C6H10CaO6·5H2O. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E326','Potassium lactate (antioxidant)','May or may not be vegan',"Potassium lactate is a compound with formula KC3H5O3, or H3C-CHOH-COOK. It is the potassium salt of lactic acid. It is produced by neutralizing lactic acid which is fermented from a sugar source. It has E number \"E326\". Potassium lactate is a liquid product that is usually 60% solids but is available at up to 78% solids. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E325','Sodium lactate','May or may not be vegan',"Sodium lactate is the sodium salt of lactic acid, and has a mild saline taste. It is produced by fermentation of a sugar source, such as corn or beets, and then, by neutralizing the resulting lactic acid to create a compound having the formula NaC3H5O3. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E324','Ethoxyquin','May or may not be vegan',"Ethoxyquin (EMQ) is a quinoline-based antioxidant used as a food preservative in certain countries and originally to control scald on pears after harvest (under commercial names such as \"Stop-Scald\"). It is used as a preservative in some pet foods to slow the development of rancidity of fats. Ethoxyquin is also used in some spices to prevent color loss due to oxidation of the natural carotenoid pigments. ",'antioxidant','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E323','Anoxomer','May or may not be vegan',"Anoxomer is a food additive with E number E323. It is a non-digestible polymeric antioxidant. It was designed to allow the introduction of established antioxidants in a non-absorbable manner in order to avoid potential health risk associated with their digestion. ",'antioxidant','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E322','Lecithin','May or may not be vegan',"Lecithin (UK: /ˈlɛsɪθɪn/, US: /ˈlɛsəθɪn/, from the Greek lekithos \"yolk\") is a generic term to designate any group of yellow-brownish fatty substances occurring in animal and plant tissues which are amphiphilic – they attract both water and fatty substances (and so are both hydrophilic and lipophilic), and are used for smoothing food textures, emulsifying, homogenizing liquid mixtures, and repelling sticking materials. ",'emulsifier','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E321','Butylated hydroxytoluene (BHT)','Vegan',"Butylated hydroxytoluene (BHT), also known as dibutylhydroxytoluene, is a lipophilic organic compound, chemically a derivative of phenol, that is useful for its antioxidant properties. U.S. regulations allow small amounts to be used as a food additive. In addition to this use, BHT is widely used to prevent oxidation in fluids (e.g. fuel, oil) and other materials where free radicals must be controlled. ",'antioxidant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E320','Butylated hydroxyanisole (BHA)','Vegan',"Butylated hydroxyanisole (BHA) is an antioxidant consisting of a mixture of two isomeric organic compounds, 2-tert-butyl-4-hydroxyanisole and 3-tert-butyl-4-hydroxyanisole. It is prepared from 4-methoxyphenol and isobutylene. It is a waxy solid used as a food additive with the E number E320. The primary use for BHA is as an antioxidant and preservative in food, food packaging, animal feed, cosmetics, rubber, and petroleum products. BHA also is commonly used in medicines, such as isotretinoin, lovastatin, and simvastatin, among others. ",'antioxidant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E319','tert-Butylhydroquinone (TBHQ)','Vegan',"tert-Butylhydroquinone (TBHQ, tertiary butylhydroquinone) is a synthetic aromatic organic compound which is a type of phenol. It is a derivative of hydroquinone, substituted with a tert-butyl group. ",'antioxidant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E318','Sodium erythorbin','Vegan',"Calcium erythorbate is a food additive. Chemically, it is the calcium salt of erythorbic acid, with the chemical formula Ca(C6H7O6)2. As an antioxidant structurally related to vitamin C, it helps improve flavor stability and prevents the formation of carcinogenic nitrosamines. ",'antioxidant','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E317','Erythorbin acid','Vegan',"Erythorbic acid (isoascorbic acid, D-araboascorbic acid) is a stereoisomer of ascorbic acid (vitamin C). It is synthesized by a reaction between methyl 2-keto-D-gluconate and sodium methoxide. It can also be synthesized from sucrose or by strains of Penicillium that have been selected for this feature. It is denoted by E number E315, and is widely used as an antioxidant in processed foods. ",'antioxidant','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E316','Sodium erythorbate','Vegan',"Sodium erythorbate (C6H7NaO6) is a food additive used predominantly in meats, poultry, and soft drinks. Chemically, it is the sodium salt of erythorbic acid. When used in processed meat such as hot dogs and beef sticks, it increases the rate at which nitrite reduces to nitric oxide, thus facilitating a faster cure and retaining the pink coloring. As an antioxidant structurally related to vitamin C, it helps improve flavor stability and prevents the formation of carcinogenic nitrosamines. When used as a food additive, its E number is E316. The use of erythorbic acid and sodium erythorbate as a food preservative has increased greatly since the U.S. Food and Drug Administration banned the use of sulfites as preservatives in foods intended to be eaten fresh (such as ingredients for fresh salads) and as food processors have responded to the fact that some people are allergic to sulfites. It can also be found in bologna, and is occasionally used in beverages, baked goods, and potato salad. ",'antioxidant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E315','Erythorbic acid','Vegan',"Erythorbic acid (isoascorbic acid, D-araboascorbic acid) is a stereoisomer of ascorbic acid (vitamin C). It is synthesized by a reaction between methyl 2-keto-D-gluconate and sodium methoxide. It can also be synthesized from sucrose or by strains of Penicillium that have been selected for this feature. It is denoted by E number E315, and is widely used as an antioxidant in processed foods. ",'antioxidant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E314','Guaiac resin','May or may not be vegan',"δ-Tocopherol is one of the chemical compounds that is considered vitamin E. As a food additive, it has E number E309. ",'antioxidant','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E313','Ethyl gallate','May or may not be vegan',"δ-Tocopherol is one of the chemical compounds that is considered vitamin E. As a food additive, it has E number E309. ",'antioxidant','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E312','Dodecyl gallate','Vegan',"δ-Tocopherol is one of the chemical compounds that is considered vitamin E. As a food additive, it has E number E309. ",'antioxidant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E311','Octyl gallate','Vegan',"δ-Tocopherol is one of the chemical compounds that is considered vitamin E. As a food additive, it has E number E309. ",'antioxidant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E310','Propyl gallate','Vegan',"δ-Tocopherol is one of the chemical compounds that is considered vitamin E. As a food additive, it has E number E309. ",'antioxidant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E309','Delta-Tocopherol (synthetic)','Vegan',"δ-Tocopherol is one of the chemical compounds that is considered vitamin E. As a food additive, it has E number E309. ",'antioxidant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E308','Gamma-Tocopherol (synthetic)','Vegan',"γ-Tocopherol is one of the chemical compounds that is considered vitamin E. As a food additive, it has E number E308. ",'antioxidant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E307','Alpha-Tocopherol (synthetic)','Vegan',"Tocopherols (/toʊˈkɒfəˌrɒl/; TCP) are a class of organic chemical compounds (more precisely, various methylated phenols), many of which have vitamin E activity. Because the vitamin activity was first identified in 1936 from a dietary fertility factor in rats, it was given the name \"tocopherol\" using the Greek words \"τόκος\" [tókos, birth], and \"φέρειν\", [phérein, to bear or carry] meaning in sum \"to carry a pregnancy,\" with the ending \"-ol\" signifying its status as a chemical alcohol. ",'antioxidant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E306','Tocopherols (Vitamin E, natural)','Vegan',"Tocopherols (/toʊˈkɒfəˌrɒl/; TCP) are a class of organic chemical compounds (more precisely, various methylated phenols), many of which have vitamin E activity. Because the vitamin activity was first identified in 1936 from a dietary fertility factor in rats, it was given the name \"tocopherol\" using the Greek words \"τόκος\" [tókos, birth], and \"φέρειν\", [phérein, to bear or carry] meaning in sum \"to carry a pregnancy,\" with the ending \"-ol\" signifying its status as a chemical alcohol. ",'antioxidant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E305','Ascorbyl stearate','May or may not be vegan',"Ascorbyl stearate (C24H42O7) is an ester formed from ascorbic acid and stearic acid. In addition to its use as a source of vitamin C, it is used as an antioxidant food additive in margarine (E number E305). The USDA limits its use to 0.02% individually or in conjunction with other antioxidants. ",'antioxidant','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E304','Fatty acid esters of ascorbic acid (Ascorbyl palmitate)','Vegan',"Ascorbyl palmitate is an ester formed from ascorbic acid and palmitic acid creating a fat-soluble form of vitamin C. In addition to its use as a source of vitamin C, it is also used as an antioxidant food additive (E number E304). It is approved for use as a food additive in the EU, the U.S., Canada, Australia, and New Zealand. ",'antioxidant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E303','Potassium ascorbate','Vegan',"Potassium ascorbate is a compound with formula KC6H7O6. It is the potassium salt of ascorbic acid (vitamin C) and a mineral ascorbate. As a food additive, it has E number E303, INS number 202. Although it is not a permitted food additive in the UK or the USA, it is approved for use in Australia and New Zealand. ",'antioxidant','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E302','Calcium ascorbate','Vegan',"Calcium ascorbate is a compound with the molecular formula CaC12H14O12. It is the calcium salt of ascorbic acid, one of the mineral ascorbates. It is approximately 10% calcium by mass. ",'antioxidant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E301','Sodium ascorbate','Vegan',"Vitamin C, also known as ascorbic acid and ascorbate, is a vitamin found in various foods and sold as a dietary supplement. It is used to prevent and treat scurvy. Vitamin C is an essential nutrient involved in the repair of tissue and the enzymatic production of certain neurotransmitters. It is required for the functioning of several enzymes and is important for immune system function. It also functions as an antioxidant. ",'antioxidant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E300','Ascorbic acid (Vitamin C)','Vegan',"Vitamin C, also known as ascorbic acid and ascorbate, is a vitamin found in various foods and sold as a dietary supplement. It is used to prevent and treat scurvy. Vitamin C is an essential nutrient involved in the repair of tissue and the enzymatic production of certain neurotransmitters. It is required for the functioning of several enzymes and is important for immune system function. It also functions as an antioxidant. ",'antioxidant','Approved in the EU.']);

    //Colour
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E182','Orcein, Orchil','May or may not be vegan',"Lithol Rubine BK is a reddish synthetic azo dye. It has the appearance of a red powder and magenta when printed. It is slightly soluble in hot water, insoluble in cold water, and insoluble in ethanol. When dissolved in dimethylformamide, its absorption maximum lies at about 442 nm. It is usually supplied as a calcium salt. It is prepared by azo coupling with 3-hydroxy-2-naphthoic acid. It is used to dye plastics, paints, printing inks, and for textile printing. It is normally used as a standard magenta in the three and four color printing processes. ",'Purple','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E181','Tannin','Vegan',"Lithol Rubine BK is a reddish synthetic azo dye. It has the appearance of a red powder and magenta when printed. It is slightly soluble in hot water, insoluble in cold water, and insoluble in ethanol. When dissolved in dimethylformamide, its absorption maximum lies at about 442 nm. It is usually supplied as a calcium salt. It is prepared by azo coupling with 3-hydroxy-2-naphthoic acid. It is used to dye plastics, paints, printing inks, and for textile printing. It is normally used as a standard magenta in the three and four color printing processes. ",'Brown','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E180','Pigment Rubine, Lithol Rubine BK','Vegan',"Lithol Rubine BK is a reddish synthetic azo dye. It has the appearance of a red powder and magenta when printed. It is slightly soluble in hot water, insoluble in cold water, and insoluble in ethanol. When dissolved in dimethylformamide, its absorption maximum lies at about 442 nm. It is usually supplied as a calcium salt. It is prepared by azo coupling with 3-hydroxy-2-naphthoic acid. It is used to dye plastics, paints, printing inks, and for textile printing. It is normally used as a standard magenta in the three and four color printing processes. ",'Red','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E175','Gold','Vegan',"Silver is a chemical element with the symbol Ag (from the Latin argentum, derived from the Proto-Indo-European h₂erǵ: \"shiny\" or \"white\") and atomic number 47. A soft, white, lustrous transition metal, it exhibits the highest electrical conductivity, thermal conductivity, and reflectivity of any metal. The metal is found in the Earth's crust in the pure, free elemental form (\"native silver\"), as an alloy with gold and other metals, and in minerals such as argentite and chlorargyrite. Most silver is produced as a byproduct of copper, gold, lead, and zinc refining. ",'Gold','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E174','Silver','Vegan',"Silver is a chemical element with the symbol Ag (from the Latin argentum, derived from the Proto-Indo-European h₂erǵ: \"shiny\" or \"white\") and atomic number 47. A soft, white, lustrous transition metal, it exhibits the highest electrical conductivity, thermal conductivity, and reflectivity of any metal. The metal is found in the Earth's crust in the pure, free elemental form (\"native silver\"), as an alloy with gold and other metals, and in minerals such as argentite and chlorargyrite. Most silver is produced as a byproduct of copper, gold, lead, and zinc refining. ",'Silver','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E173','Aluminium','Vegan',"",'Silver to grey','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E172','Iron oxides and iron hydroxides','Vegan',"Titanium dioxide, also known as titanium(IV) oxide or titania, is the naturally occurring oxide of titanium, chemical formula TiO2. When used as a pigment, it is called titanium white, Pigment White 6 (PW6), or CI 77891. Generally, it is sourced from ilmenite, rutile, and anatase. It has a wide range of applications, including paint, sunscreen, and food coloring. When used as a food coloring, it has E number E171. World production in 2014 exceeded 9 million metric tons. It has been estimated that titanium dioxide is used in two-thirds of all pigments, and pigments based on the oxide have been valued at \$13.2 billion. ",'Brown','Approved in the EU. Approved in the US for sausage casings.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E171','Titanium dioxide','Vegan',"Titanium dioxide, also known as titanium(IV) oxide or titania, is the naturally occurring oxide of titanium, chemical formula TiO2. When used as a pigment, it is called titanium white, Pigment White 6 (PW6), or CI 77891. Generally, it is sourced from ilmenite, rutile, and anatase. It has a wide range of applications, including paint, sunscreen, and food coloring. When used as a food coloring, it has E number E171. World production in 2014 exceeded 9 million metric tons. It has been estimated that titanium dioxide is used in two-thirds of all pigments, and pigments based on the oxide have been valued at \$13.2 billion. ",'White','Approved in the EU. Approved in the US.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E170','Calcium carbonate, Chalk','Vegan',"Anthocyanins (also anthocyans; from Greek: ἄνθος (anthos) \"flower\" and κυάνεος/κυανοῦς kyaneos/kyanous \"dark blue\") are water-soluble vacuolar pigments that, depending on their pH, may appear red, purple, blue or black. Food plants rich in anthocyanins include the blueberry, raspberry, black rice, and black soybean, among many others that are red, blue, purple, or black. Some of the colors of autumn leaves are derived from anthocyanins. ",'White','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E164','Saffron','May or may not be vegan',"Anthocyanins (also anthocyans; from Greek: ἄνθος (anthos) \"flower\" and κυάνεος/κυανοῦς kyaneos/kyanous \"dark blue\") are water-soluble vacuolar pigments that, depending on their pH, may appear red, purple, blue or black. Food plants rich in anthocyanins include the blueberry, raspberry, black rice, and black soybean, among many others that are red, blue, purple, or black. Some of the colors of autumn leaves are derived from anthocyanins. ",'Orange-red[colour?]','Approved in the US.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E163','Anthocyanins','Vegan',"Anthocyanins (also anthocyans; from Greek: ἄνθος (anthos) \"flower\" and κυάνεος/κυανοῦς kyaneos/kyanous \"dark blue\") are water-soluble vacuolar pigments that, depending on their pH, may appear red, purple, blue or black. Food plants rich in anthocyanins include the blueberry, raspberry, black rice, and black soybean, among many others that are red, blue, purple, or black. Some of the colors of autumn leaves are derived from anthocyanins. ",'pH dependent(Red, green and purple ranges)','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E162','Beetroot Red, Betanin','Vegan',"Betanin, or Beetroot Red, is a red glycosidic food dye obtained from beets; its aglycone, obtained by hydrolyzing away the glucose molecule, is betanidin. As a food additive, its E number is E162. ",'Red','Approved in the EU. Approved in the US.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E161j','Astaxanthin','May or may not be vegan',"Astaxanthin /æstəˈzænθɪn/ is a keto-carotenoid. It belongs to a larger class of chemical compounds known as terpenes (as a tetraterpenoid) built from five carbon precursors, isopentenyl diphosphate, and dimethylallyl diphosphate. Astaxanthin is classified as a xanthophyll (originally derived from a word meaning \"yellow leaves\" since yellow plant leaf pigments were the first recognized of the xanthophyll family of carotenoids), but currently employed to describe carotenoid compounds that have oxygen-containing components, hydroxyl (-OH) or ketone (C=O), such as zeaxanthin and canthaxanthin. Indeed, astaxanthin is a metabolite of zeaxanthin and/or canthaxanthin, containing both hydroxyl and ketone functional groups. ",'Red','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E161i','Citranaxanthin','May or may not be vegan',"Citranaxanthin is a carotenoid pigment used as a food additive under the E number E161i as a food coloring. There are natural sources of citranaxanthin, but it is generally prepared synthetically. It is used as an animal feed additive to impart a yellow color to chicken fat and egg yolks. ",'Deep violet','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E161h','Zeaxanthin','May or may not be vegan',"Zeaxanthin is one of the most common carotenoid alcohols found in nature. It is important in the xanthophyll cycle. Synthesized in plants and some micro-organisms, it is the pigment that gives paprika (made from bell peppers), corn, saffron, wolfberries, and many other plants and microbes their characteristic color. ",'Orange-red','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E161g','Canthaxanthin','May or may not be vegan',"Canthaxanthin /ˌkænθəˈzænθɪn/ (listen) is a keto-carotenoid pigment widely distributed in nature. Carotenoids belong to a larger class of phytochemicals known as terpenoids. The chemical formula of canthaxanthin is C40H52O2. It was first isolated in edible mushrooms. It has also been found in green algae, bacteria, crustaceans, and bioaccumulates in fish such as carp, golden mullet, seabream and trush wrasse. ",'Violet','Approved in the EU. Approved in the US.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E161f','Rhodoxanthin','Vegan',"Rhodoxanthin is a xanthophyll pigment with a purple color that is found in small quantities in a variety of plants including Taxus baccata and Lonicera morrowii. It is also found in the feathers of some birds. As a food additive it is used under the E number E161f as a food coloring. It is not approved for use in the EU or US; however, it is approved in Australia and New Zealand (where it is listed under its INS number 161f). ",'Purple','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E161e','Violaxanthin','Vegan',"Violaxanthin is a natural xanthophyll pigment with an orange color found in a variety of plants including pansies. It is biosynthesized from zeaxanthin by epoxidation. As a food additive it is used under the E number E161e as a food coloring; it is not approved for use in the EU or USA however is approved in Australia and New Zealand (where it is listed under its INS number 161e). ",'Orange','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E161d','Rubixanthin','Vegan',"Rubixanthin, or natural yellow 27, is a natural xanthophyll pigment with a red-orange color found in rose hips. As a food additive it used under the E number E161d as a food coloring; it is not approved for use in the USA or EU but is approved in Australia and New Zealand where it is listed as 161d. ",'Orange-red','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E161c','Cryptoxanthin','Vegan',"Cryptoxanthin is a natural carotenoid pigment. It has been isolated from a variety of sources including the petals and flowers of plants in the genus Physalis, orange rind, papaya, egg yolk, butter, apples, and bovine blood serum. ",'Orange-red','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E161b','Lutein','Vegan',"Lutein (/ˈljuːtiɪn, -tiːn/; from Latin luteus meaning \"yellow\") is a xanthophyll and one of 600 known naturally occurring carotenoids. Lutein is synthesized only by plants, and like other xanthophylls is found in high quantities in green leafy vegetables such as spinach, kale and yellow carrots. In green plants, xanthophylls act to modulate light energy and serve as non-photochemical quenching agents to deal with triplet chlorophyll (an excited form of chlorophyll), which is overproduced at very high light levels, during photosynthesis. See xanthophyll cycle for this topic. ",'Orange-red to yellow','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E161a','Flavoxanthin','Vegan',"Flavoxanthin is a natural xanthophyll pigment with a golden-yellow color found in small quantities in a variety of plants. As a food additive it used under the E number E161a as a food coloring although it is not approved for use in the EU or USA. It is listed as food additive 161a in Australia and New Zealand where it is approved for usage as an ingredient in food products. ",'Golden-yellow and brownish','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E160f','Ethyl ester of beta-apo-8-carotenic acid (C 30)','Vegan',"Food orange 7, the ethyl ester of beta-apo-8'-carotenic acid, is a carotenoid with an orange-red color. It is found in small quantities in some plants, but is often produced commercially from apocarotenal (E160e). It is used as a food coloring under the E number E160f and is approved for use in the EU and Australia and New Zealand  where it is listed as food additive 160f; it is banned in the United States. ",'Orange-red to yellow','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E160e','Beta-apo-8-carotenal (C 30)','Vegan',"Apocarotenal, or trans-β-apo-8'-carotenal, is a carotenoid found in spinach and citrus fruits. Like other carotenoids, apocarotenal plays a role as a precursor of vitamin A, even though it has 50% less pro-vitamin A activity than β-carotene. The empirical chemical formula for apocarotenal is C30H40O. ",'Orange-red to yellow','Approved in the EU. Approved in the US.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E160d','Lycopene','Vegan',"Lycopene (from the neo-Latin Lycopersicum, the tomato species) is a bright red carotenoid hydrocarbon found in tomatoes and other red fruits and vegetables, such as red carrots, watermelons, grapefruits, and papayas, but it is not present in strawberries or cherries. Although lycopene is chemically a carotene, it has no vitamin A activity. Foods that are not red may also contain lycopene, such as asparagus and parsley. ",'Bright to deep red','Approved in the EU. Approved in the US.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E160c','Paprika oleoresin: (i) capsanthin, (ii) capsorubin','Vegan',"Paprika oleoresin (also known as paprika extract and oleoresin paprika) is an oil-soluble extract from the fruits of Capsicum annuum or Capsicum frutescens, and is primarily used as a colouring and/or flavouring in food products. It is composed of vegetable oil (often in the range of 97% to 98%), capsaicin, the main flavouring compound giving pungency in higher concentrations, and capsanthin and capsorubin, the main colouring compounds (among other carotenoids). It is much milder than capsicum oleoresin, often containing no capsaicin at all. ",'Red','Approved in the EU. Approved in the US.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E160b','Annatto, bixin, norbixin','Vegan',"Annatto (/əˈnætoʊ/ or /əˈnɑːtoʊ/) is an orange-red condiment and food coloring derived from the seeds of the achiote tree (Bixa orellana) native to tropical regions from Mexico to Brazil. It is often used to impart a yellow or orange color to foods, but sometimes also for its flavor and aroma. Its scent is described as \"slightly peppery with a hint of nutmeg\" and flavor as \"slightly nutty, sweet and peppery\". ",'Orange','Approved in the EU. Approved in the US.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E160a','Alpha-carotene, Beta-carotene, Gamma-carotene','Vegan',"The term carotene (also carotin, from the Latin carota, \"carrot\") is used for many related unsaturated hydrocarbon substances having the formula C40Hx, which are synthesized by plants but in general cannot be made by animals (with the exception of some aphids and spider mites which acquired the synthesizing genes from fungi). Carotenes are photosynthetic pigments important for photosynthesis. Carotenes contain no oxygen atoms. They absorb ultraviolet, violet, and blue light and scatter orange or red light, and (in low concentrations) yellow light. ",'Yellow-orange to brown','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E155','Brown HT (chocolate brown HT)','Vegan',"Brown HT, also called Chocolate Brown HT, Food Brown 3, and C.I. 20285, is a brown synthetic coal tar diazo dye. ",'Brown','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E154','Brown FK (kipper brown)','Vegan',"Brown FK, also called Kipper Brown, Chocolate Brown FK, and C.I. Food Brown 1, is a brown mixture of six synthetic azo dyes, with addition of sodium chloride, and/or sodium sulfate. It is very soluble in water. When used as a food dye, its E number is E154. ",'Brown','Approved in the EU for dyeing kippers only, however appears to no longer be used.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E153','Vegetable carbon','May or may not be vegan',"Carbon black (subtypes are acetylene black, channel black, furnace black, lamp black and thermal black) is a material produced by the incomplete combustion of heavy petroleum products such as FCC tar, coal tar, or ethylene cracking tar. Carbon black is a form of paracrystalline carbon that has a high surface-area-to-volume ratio, albeit lower than that of activated carbon. It is dissimilar to soot in its much higher surface-area-to-volume ratio and significantly lower (negligible and non-bioavailable) polycyclic aromatic hydrocarbon (PAH) content. However, carbon black is widely used as a model compound for diesel soot for diesel oxidation experiments. Carbon black is mainly used as a reinforcing filler in tires and other rubber products. In plastics, paints, and inks, carbon black is used as a color pigment. ",'Black','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E152','Carbon black (hydrocarbon)','Vegan',"Black 7984, Food Black 2, or C.I. 27755, is a brown-to-black synthetic diazo dye. It is often used as the tetrasodium salt. ",'Black','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E151','Black PN, Brilliant Black BN','Vegan',"Brilliant Black BN, Brilliant Black PN, Brilliant Black A, Black PN, Food Black 1, Naphthol Black, C.I. Food Black 1, or C.I. 28440, is a synthetic black diazo dye. It is soluble in water. It usually comes as tetrasodium salt. It has the appearance of solid, fine powder or granules. Calcium and potassium salts are allowed as well. ",'Black','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E150d','Sulfite ammonia caramel','Vegan',"Caramel color or caramel coloring is a water-soluble food coloring. It is made by heat treatment of carbohydrates (sugars), in general in the presence of acids, alkalis, or salts, in a process called caramelization. It is more fully oxidized than caramel candy, and has an odor of burnt sugar and a somewhat bitter taste. Its color ranges from pale yellow to amber to dark brown. ",'Brown','Approved in the EU. Approved in the US.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E150c','Ammonia caramel','Vegan',"Caramel color or caramel coloring is a water-soluble food coloring. It is made by heat treatment of carbohydrates (sugars), in general in the presence of acids, alkalis, or salts, in a process called caramelization. It is more fully oxidized than caramel candy, and has an odor of burnt sugar and a somewhat bitter taste. Its color ranges from pale yellow to amber to dark brown. ",'Brown','Approved in the EU. Approved in the US.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E150b','Caustic sulfite caramel','Vegan',"Caramel color or caramel coloring is a water-soluble food coloring. It is made by heat treatment of carbohydrates (sugars), in general in the presence of acids, alkalis, or salts, in a process called caramelization. It is more fully oxidized than caramel candy, and has an odor of burnt sugar and a somewhat bitter taste. Its color ranges from pale yellow to amber to dark brown. ",'Brown','Approved in the EU. Approved in the US.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E150a','Plain caramel','Vegan',"Caramel color or caramel coloring is a water-soluble food coloring. It is made by heat treatment of carbohydrates (sugars), in general in the presence of acids, alkalis, or salts, in a process called caramelization. It is more fully oxidized than caramel candy, and has an odor of burnt sugar and a somewhat bitter taste. Its color ranges from pale yellow to amber to dark brown. ",'Brown','Approved in the EU. Approved in the US.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E143','Fast Green FCF (FD&C Green 3)','Vegan',"Fast Green FCF, also called Food green 3, FD&C Green No. 3, Green 1724, Solid Green FCF, and C.I. 42053, is a turquoise triarylmethane food dye. Its E number is E143. ",'Green','Approved in the US. Banned in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E142','Green S','Vegan',"Green S is a green synthetic coal tar triarylmethane dye with the molecular formula C27H25N2O7S2Na. ",'Green','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E141','Copper complexes of chlorophylls and chlorophyllins (i) Copper complexes of chlorophylls (ii) Copper complexes of chlorophyllins','Vegan',"Brilliant Blue FCF (Blue 1) is a synthetic organic compound used primarily as a blue colorant for processed foods, medications, dietary supplements, and cosmetics. It is classified as a triarylmethane dye and is known under various names, such as FD&C Blue No. 1 or Acid Blue 9. It is denoted by E number E133 and has a color index of 42090. It has the appearance of a blue powder and is soluble in water and glycerol, with a maximum absorption at about 628 nanometers. It is one of the oldest FDA-approved color additives and is generally considered nontoxic and safe. ",'Green','Approved in the EU. Approved in the US.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E140','Chlorophylls and Chlorophyllins: (i) Chlorophylls (ii) Chlorophyllins','Vegan',"Brilliant Blue FCF (Blue 1) is a synthetic organic compound used primarily as a blue colorant for processed foods, medications, dietary supplements, and cosmetics. It is classified as a triarylmethane dye and is known under various names, such as FD&C Blue No. 1 or Acid Blue 9. It is denoted by E number E133 and has a color index of 42090. It has the appearance of a blue powder and is soluble in water and glycerol, with a maximum absorption at about 628 nanometers. It is one of the oldest FDA-approved color additives and is generally considered nontoxic and safe. ",'Green','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E133','Brilliant Blue FCF (FD&C Blue 1)','Vegan',"Brilliant Blue FCF (Blue 1) is a synthetic organic compound used primarily as a blue colorant for processed foods, medications, dietary supplements, and cosmetics. It is classified as a triarylmethane dye and is known under various names, such as FD&C Blue No. 1 or Acid Blue 9. It is denoted by E number E133 and has a color index of 42090. It has the appearance of a blue powder and is soluble in water and glycerol, with a maximum absorption at about 628 nanometers. It is one of the oldest FDA-approved color additives and is generally considered nontoxic and safe. ",'Reddish blue','Approved in the EU. Approved in the US.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E132','Indigo carmine (indigotine, FD&C Blue 2)','Vegan',"Indigo carmine, or 5,5′-indigodisulfonic acid sodium salt, is an organic salt derived from indigo by sulfonation, which renders the compound soluble in water. It is approved for use as a food colorant in the U.S and E.U. It has the E number E132. It is also a pH indicator. ",'Indigo','Approved in the EU. Approved in the US.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E131','Patent Blue V','Vegan',"Patent Blue V, also called Food Blue 5, Sulphan Blue, Acid Blue 3, L-Blau 3, C-Blau 20, Patentblau V, Sky Blue, or C.I. 42051, is a sky blue synthetic triphenylmethane dye used as a food coloring. As a food additive, it has E number E131. It is a sodium or calcium salt of [4-(α-(4-diethylaminophenyl)-5-hydroxy- 2,4-disulfophenylmethylidene)-2,5-cyclohexadien-1-ylidene] diethylammonium hydroxide inner salt. ",'Dark blue','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E130','Indanthrene blue RS','Vegan',"Ponceau 4R (known by more than 100 synonyms,:460–461 including as C.I. 16255, Cochineal Red A, C.I. Acid Red 18, Brilliant Scarlet 3R, Brilliant Scarlet 4R, New Coccine,:460 is a synthetic colourant that may be used as a food colouring. It is denoted by E Number E124. Its chemical name is 1-(4-sulfo-1-napthylazo)- 2-napthol- 6,8-disulfonic acid, trisodium salt. Ponceau (17th century French for \"poppy-coloured\") is the generic name for a family of azo dyes. ",'Blue','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E129','Allura Red AC (FD&C Red 40)','Vegan',"Ponceau 4R (known by more than 100 synonyms,:460–461 including as C.I. 16255, Cochineal Red A, C.I. Acid Red 18, Brilliant Scarlet 3R, Brilliant Scarlet 4R, New Coccine,:460 is a synthetic colourant that may be used as a food colouring. It is denoted by E Number E124. Its chemical name is 1-(4-sulfo-1-napthylazo)- 2-napthol- 6,8-disulfonic acid, trisodium salt. Ponceau (17th century French for \"poppy-coloured\") is the generic name for a family of azo dyes. ",'Red','Approved in the EU. Banned in Switzerland. Undergoing a voluntary phase out in the UK. Approved in the US.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E128','Red 2G','Vegan',"Ponceau 4R (known by more than 100 synonyms,:460–461 including as C.I. 16255, Cochineal Red A, C.I. Acid Red 18, Brilliant Scarlet 3R, Brilliant Scarlet 4R, New Coccine,:460 is a synthetic colourant that may be used as a food colouring. It is denoted by E Number E124. Its chemical name is 1-(4-sulfo-1-napthylazo)- 2-napthol- 6,8-disulfonic acid, trisodium salt. Ponceau (17th century French for \"poppy-coloured\") is the generic name for a family of azo dyes. ",'Red or pink','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E127','Erythrosine (FD&C Red 3)','Vegan',"Ponceau 4R (known by more than 100 synonyms,:460–461 including as C.I. 16255, Cochineal Red A, C.I. Acid Red 18, Brilliant Scarlet 3R, Brilliant Scarlet 4R, New Coccine,:460 is a synthetic colourant that may be used as a food colouring. It is denoted by E Number E124. Its chemical name is 1-(4-sulfo-1-napthylazo)- 2-napthol- 6,8-disulfonic acid, trisodium salt. Ponceau (17th century French for \"poppy-coloured\") is the generic name for a family of azo dyes. ",'Red','Approved in the EU. Approved in the US except for lake variant.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E126','Ponceau 6R','Vegan',"Ponceau 4R (known by more than 100 synonyms,:460–461 including as C.I. 16255, Cochineal Red A, C.I. Acid Red 18, Brilliant Scarlet 3R, Brilliant Scarlet 4R, New Coccine,:460 is a synthetic colourant that may be used as a food colouring. It is denoted by E Number E124. Its chemical name is 1-(4-sulfo-1-napthylazo)- 2-napthol- 6,8-disulfonic acid, trisodium salt. Ponceau (17th century French for \"poppy-coloured\") is the generic name for a family of azo dyes. ",'Red','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E125','Ponceau SX, Scarlet GN','Vegan',"Ponceau 4R (known by more than 100 synonyms,:460–461 including as C.I. 16255, Cochineal Red A, C.I. Acid Red 18, Brilliant Scarlet 3R, Brilliant Scarlet 4R, New Coccine,:460 is a synthetic colourant that may be used as a food colouring. It is denoted by E Number E124. Its chemical name is 1-(4-sulfo-1-napthylazo)- 2-napthol- 6,8-disulfonic acid, trisodium salt. Ponceau (17th century French for \"poppy-coloured\") is the generic name for a family of azo dyes. ",'Red','Only permitted for externally applied drugs and cosmetics in the US.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E124','Ponceau 4R (Cochineal Red A, Brilliant Scarlet 4R)','Vegan',"Ponceau 4R (known by more than 100 synonyms,:460–461 including as C.I. 16255, Cochineal Red A, C.I. Acid Red 18, Brilliant Scarlet 3R, Brilliant Scarlet 4R, New Coccine,:460 is a synthetic colourant that may be used as a food colouring. It is denoted by E Number E124. Its chemical name is 1-(4-sulfo-1-napthylazo)- 2-napthol- 6,8-disulfonic acid, trisodium salt. Ponceau (17th century French for \"poppy-coloured\") is the generic name for a family of azo dyes. ",'Red','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E123','Amaranth (FD&C Red 2)','Vegan',"Azorubine is an azo dye consisting of two naphthalene subunits. It is a red solid. It is mainly used in foods that are heat-treated after fermentation. It has E number E122. ",'Red','Approved in the EU. Banned in the United States.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E122','Carmoisine (azorubine)','Vegan',"Azorubine is an azo dye consisting of two naphthalene subunits. It is a red solid. It is mainly used in foods that are heat-treated after fermentation. It has E number E122. ",'Red to maroon','Approved in the EU. Undergoing a voluntary phase-out in the UK. Currently banned in Canada, Japan, Norway, USA . EU currently evaluating health risks.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E121','Citrus Red 2','Vegan',"Flavin mononucleotide (FMN), or riboflavin-5′-phosphate, is a biomolecule produced from riboflavin (vitamin B2) by the enzyme riboflavin kinase and functions as prosthetic group of various oxidoreductases including NADH dehydrogenase as well as cofactor in biological blue-light photo receptors. During the catalytic cycle, a reversible interconversion of the oxidized (FMN), semiquinone (FMNH•) and reduced (FMNH2) forms occurs in the various oxidoreductases. FMN is a stronger oxidizing agent than NAD and is particularly useful because it can take part in both one- and two-electron transfers. In its role as blue-light photo receptor, (oxidized) FMN stands out from the 'conventional' photo receptors as the signaling state and not an E/Z isomerization. ",'Dark red','Approved in the United States only for use in colouring the skin of oranges.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E120','Cochineal, Carminic acid, Carmine (Natural Red 4)','Not Vegan',"Flavin mononucleotide (FMN), or riboflavin-5′-phosphate, is a biomolecule produced from riboflavin (vitamin B2) by the enzyme riboflavin kinase and functions as prosthetic group of various oxidoreductases including NADH dehydrogenase as well as cofactor in biological blue-light photo receptors. During the catalytic cycle, a reversible interconversion of the oxidized (FMN), semiquinone (FMNH•) and reduced (FMNH2) forms occurs in the various oxidoreductases. FMN is a stronger oxidizing agent than NAD and is particularly useful because it can take part in both one- and two-electron transfers. In its role as blue-light photo receptor, (oxidized) FMN stands out from the 'conventional' photo receptors as the signaling state and not an E/Z isomerization. ",'Crimson','Approved in the EU. Approved in the US.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E111','Orange GGN','Vegan',"Flavin mononucleotide (FMN), or riboflavin-5′-phosphate, is a biomolecule produced from riboflavin (vitamin B2) by the enzyme riboflavin kinase and functions as prosthetic group of various oxidoreductases including NADH dehydrogenase as well as cofactor in biological blue-light photo receptors. During the catalytic cycle, a reversible interconversion of the oxidized (FMN), semiquinone (FMNH•) and reduced (FMNH2) forms occurs in the various oxidoreductases. FMN is a stronger oxidizing agent than NAD and is particularly useful because it can take part in both one- and two-electron transfers. In its role as blue-light photo receptor, (oxidized) FMN stands out from the 'conventional' photo receptors as the signaling state and not an E/Z isomerization. ",'Orange','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E110','Sunset Yellow FCF (Orange Yellow S, FD&C Yellow 6)','Vegan',"Flavin mononucleotide (FMN), or riboflavin-5′-phosphate, is a biomolecule produced from riboflavin (vitamin B2) by the enzyme riboflavin kinase and functions as prosthetic group of various oxidoreductases including NADH dehydrogenase as well as cofactor in biological blue-light photo receptors. During the catalytic cycle, a reversible interconversion of the oxidized (FMN), semiquinone (FMNH•) and reduced (FMNH2) forms occurs in the various oxidoreductases. FMN is a stronger oxidizing agent than NAD and is particularly useful because it can take part in both one- and two-electron transfers. In its role as blue-light photo receptor, (oxidized) FMN stands out from the 'conventional' photo receptors as the signaling state and not an E/Z isomerization. ",'Yellow-orange','Approved in the EU. Banned in Norway. Products in the EU require warnings and its use is being phased out. Approved in the US.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E107','Yellow 2G','Vegan',"Flavin mononucleotide (FMN), or riboflavin-5′-phosphate, is a biomolecule produced from riboflavin (vitamin B2) by the enzyme riboflavin kinase and functions as prosthetic group of various oxidoreductases including NADH dehydrogenase as well as cofactor in biological blue-light photo receptors. During the catalytic cycle, a reversible interconversion of the oxidized (FMN), semiquinone (FMNH•) and reduced (FMNH2) forms occurs in the various oxidoreductases. FMN is a stronger oxidizing agent than NAD and is particularly useful because it can take part in both one- and two-electron transfers. In its role as blue-light photo receptor, (oxidized) FMN stands out from the 'conventional' photo receptors as the signaling state and not an E/Z isomerization. ",'Yellow','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E106','Riboflavin-5-Sodium Phosphate','Vegan',"Flavin mononucleotide (FMN), or riboflavin-5′-phosphate, is a biomolecule produced from riboflavin (vitamin B2) by the enzyme riboflavin kinase and functions as prosthetic group of various oxidoreductases including NADH dehydrogenase as well as cofactor in biological blue-light photo receptors. During the catalytic cycle, a reversible interconversion of the oxidized (FMN), semiquinone (FMNH•) and reduced (FMNH2) forms occurs in the various oxidoreductases. FMN is a stronger oxidizing agent than NAD and is particularly useful because it can take part in both one- and two-electron transfers. In its role as blue-light photo receptor, (oxidized) FMN stands out from the 'conventional' photo receptors as the signaling state and not an E/Z isomerization. ",'Yellow','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E105','Fast Yellow AB','Vegan',"Quinoline Yellow WS is a mixture of organic compounds derived from the dye Quinoline Yellow SS (Spirit Soluble). Owing to the presence of sulfonate groups, the WS dyes are water-soluble (WS). It is a mixture of disulfonates (principally), monosulfonates and trisulfonates of 2-(2-quinolyl)indan-1,3-dione with a maximum absorption wavelength of 416 nm.p. 119 ",'Yellow','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E104','Quinoline Yellow WS','Vegan',"Quinoline Yellow WS is a mixture of organic compounds derived from the dye Quinoline Yellow SS (Spirit Soluble). Owing to the presence of sulfonate groups, the WS dyes are water-soluble (WS). It is a mixture of disulfonates (principally), monosulfonates and trisulfonates of 2-(2-quinolyl)indan-1,3-dione with a maximum absorption wavelength of 416 nm.p. 119 ",'Dull or greenish yellow','Approved in the EU. Undergoing a voluntary phase-out in the UK.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E103','Alkannin','Vegan',"Alkannin is a natural dye that is obtained from the extracts of plants from the borage family Alkanna tinctoria that are found in the south of France. The dye is used as a food coloring and in cosmetics. It is used as a red-brown food additive in regions such as Australia, and is designated in Europe as the E number E103, but is no longer approved for use. Alkannin has a deep red color in a greasy or oily environment and a violet color in an alkaline environment. ",'Red-brown','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E102','Tartrazine (FD&C Yellow 5)','Vegan',"Tartrazine is a synthetic lemon yellow azo dye primarily used as a food coloring. It is also known as E number E102, C.I. 19140, FD&C Yellow 5, Acid Yellow 23, Food Yellow 4, and trisodium 1-(4-sulfonatophenyl)-4-(4-sulfonatophenylazo)-5-pyrazolone-3-carboxylate). ",'Yellow','Approved in the EU. Approved in the US.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E101a','Riboflavin-5-Phosphate','Vegan',"Flavin mononucleotide (FMN), or riboflavin-5′-phosphate, is a biomolecule produced from riboflavin (vitamin B2) by the enzyme riboflavin kinase and functions as prosthetic group of various oxidoreductases including NADH dehydrogenase as well as cofactor in biological blue-light photo receptors. During the catalytic cycle, a reversible interconversion of the oxidized (FMN), semiquinone (FMNH•) and reduced (FMNH2) forms occurs in the various oxidoreductases. FMN is a stronger oxidizing agent than NAD and is particularly useful because it can take part in both one- and two-electron transfers. In its role as blue-light photo receptor, (oxidized) FMN stands out from the 'conventional' photo receptors as the signaling state and not an E/Z isomerization. ",'Yellow-orange','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E101','Riboflavin (Vitamin B2), formerly called lactoflavin','Vegan',"",'Yellow-orange','Approved in the EU. Approved in the US.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E100','Curcumin (from turmeric)','Vegan',"",'Yellow-orange','Approved in the EU. Approved in the US.']);

    //Flavour
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E650','Zinc acetate','May or may not be vegan',"Glycine (symbol Gly or G; /ˈɡlaɪsiːn/) is an amino acid that has a single hydrogen atom as its side chain. It is the simplest amino acid (since carbamic acid is unstable), with the chemical formula NH2‐CH2‐COOH. Glycine is one of the proteinogenic amino acids. It is encoded by all the codons starting with GG (GGU, GGC, GGA, GGG). Glycine is integral to the formation of alpha-helices in secondary protein structure due to its compact form. For the same reason, it is the most abundant amino acid in collagen triple-helices. Glycine is also an inhibitory neurotransmitter - interference with its release within the spinal cord (such as during a Clostridium tetani infection) can cause spastic paralysis due to uninhibited muscle contraction. ",'flavour enhancer','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E640','Glycine and its sodium salt','May or may not be vegan',"Glycine (symbol Gly or G; /ˈɡlaɪsiːn/) is an amino acid that has a single hydrogen atom as its side chain. It is the simplest amino acid (since carbamic acid is unstable), with the chemical formula NH2‐CH2‐COOH. Glycine is one of the proteinogenic amino acids. It is encoded by all the codons starting with GG (GGU, GGC, GGA, GGG). Glycine is integral to the formation of alpha-helices in secondary protein structure due to its compact form. For the same reason, it is the most abundant amino acid in collagen triple-helices. Glycine is also an inhibitory neurotransmitter - interference with its release within the spinal cord (such as during a Clostridium tetani infection) can cause spastic paralysis due to uninhibited muscle contraction. ",'flavour enhancer','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E637','Ethyl maltol','Vegan',"Disodium 5'-ribonucleotides, E number E635, is a flavor enhancer which is synergistic with glutamates in creating the taste of umami. It is a mixture of disodium inosinate (IMP) and disodium guanylate (GMP) and is often used where a food already contains natural glutamates (as in meat extract) or added monosodium glutamate (MSG). It is primarily used in flavored noodles, snack foods, chips, crackers, sauces and fast foods. It is produced by combining the sodium salts of the natural compounds guanylic acid (E626) and inosinic acid (E630). ",'flavour enhancer','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E636','Maltol','Vegan',"Disodium 5'-ribonucleotides, E number E635, is a flavor enhancer which is synergistic with glutamates in creating the taste of umami. It is a mixture of disodium inosinate (IMP) and disodium guanylate (GMP) and is often used where a food already contains natural glutamates (as in meat extract) or added monosodium glutamate (MSG). It is primarily used in flavored noodles, snack foods, chips, crackers, sauces and fast foods. It is produced by combining the sodium salts of the natural compounds guanylic acid (E626) and inosinic acid (E630). ",'flavour enhancer','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E635','Disodium 5-ribonucleotides','May or may not be vegan',"Disodium 5'-ribonucleotides, E number E635, is a flavor enhancer which is synergistic with glutamates in creating the taste of umami. It is a mixture of disodium inosinate (IMP) and disodium guanylate (GMP) and is often used where a food already contains natural glutamates (as in meat extract) or added monosodium glutamate (MSG). It is primarily used in flavored noodles, snack foods, chips, crackers, sauces and fast foods. It is produced by combining the sodium salts of the natural compounds guanylic acid (E626) and inosinic acid (E630). ",'flavour enhancer','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E634','Calcium 5-ribonucleotides','Vegan',"Disodium inosinate (E631) is the disodium salt of inosinic acid with the chemical formula C10H11N4Na2O8P. It is used as a food additive and often found in instant noodles, potato chips, and a variety of other snacks. Although it can be obtained from bacterial fermentation of sugars, it is often commercially prepared. ",'flavour enhancer','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E633','Calcium inosinate','Vegan',"Disodium inosinate (E631) is the disodium salt of inosinic acid with the chemical formula C10H11N4Na2O8P. It is used as a food additive and often found in instant noodles, potato chips, and a variety of other snacks. Although it can be obtained from bacterial fermentation of sugars, it is often commercially prepared. ",'flavour enhancer','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E632','Dipotassium inosinate','Vegan',"Disodium inosinate (E631) is the disodium salt of inosinic acid with the chemical formula C10H11N4Na2O8P. It is used as a food additive and often found in instant noodles, potato chips, and a variety of other snacks. Although it can be obtained from bacterial fermentation of sugars, it is often commercially prepared. ",'flavour enhancer','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E631','Disodium inosinate','May or may not be vegan',"Disodium inosinate (E631) is the disodium salt of inosinic acid with the chemical formula C10H11N4Na2O8P. It is used as a food additive and often found in instant noodles, potato chips, and a variety of other snacks. Although it can be obtained from bacterial fermentation of sugars, it is often commercially prepared. ",'flavour enhancer','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E630','Inosinic acid','Vegan',"Inosinic acid or inosine monophosphate (IMP) is a nucleoside monophosphate. Widely used as a flavor enhancer, it is typically obtained from chicken byproducts or other meat industry waste. Inosinic acid is important in metabolism. It is the ribonucleotide of hypoxanthine and the first nucleotide formed during the synthesis of purine. It is formed by the deamination of adenosine monophosphate by AMP deaminase, and is hydrolysed to inosine. IMP is an intermediate ribonucleoside monophosphate in purine metabolism. The enzyme deoxyribonucleoside triphosphate pyrophosphohydrolase, encoded by YJR069C in Saccharomyces cerevisiae and containing (d)ITPase and (d)XTPase activities, hydrolyzes inosine triphosphate (ITP) releasing pyrophosphate and IMP. ",'flavour enhancer','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E629','Calcium guanylate','Vegan',"Calcium guanylate is a compound with formula Ca(C10H12O4N5PO4). It is the calcium salt of guanylic acid. ",'flavour enhancer','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E628','Dipotassium guanylate','Vegan',"Dipotassium guanylate is a compound with formula K2(C10H12O4N5PO4). It is a potassium salt of guanylic acid. ",'flavour enhancer','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E627','Disodium guanylate, sodium guanylate','May or may not be vegan',"Disodium guanylate, also known as sodium 5'-guanylate and disodium 5'-guanylate, is a natural sodium salt of the flavor enhancing nucleotide guanosine monophosphate (GMP). Disodium guanylate is a food additive with the E number E627. It is commonly used in conjunction with glutamic acid. ",'flavour enhancer','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E626','Guanylic acid','Vegan',"Guanosine monophosphate (GMP), also known as 5'-guanidylic acid or guanylic acid (conjugate base guanylate), is a nucleotide that is used as a monomer in RNA. It is an ester of phosphoric acid with the nucleoside guanosine. GMP consists of the phosphate group, the pentose sugar ribose, and the nucleobase guanine; hence it is a ribonucleoside monophosphate. Guanosine monophosphate is commercially produced by microbial fermentation. It is known as E number reference E626. ",'flavour enhancer','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E625','Magnesium diglutamate','Vegan',"Magnesium diglutamate is a compound with formula Mg(C5H8NO4)2. It is a magnesium acid salt of glutamic acid. ",'flavour enhancer','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E624','Monoammonium glutamate','Vegan',"Monoammonium glutamate is a compound with formula NH4C5H8NO4. It is an ammonium acid salt of glutamic acid. ",'flavour enhancer','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E623','Calcium diglutamate','Vegan',"Calcium diglutamate, sometimes abbreviated CDG and also called calcium glutamate, is a compound with formula Ca(C5H8NO4)2. It is a calcium acid salt of glutamic acid. CDG is a flavor enhancer (E number E623) — it is the calcium analog of monosodium glutamate (MSG). Because the glutamate is the actual flavor-enahancer, CDG has the same flavor-enhancing properties as MSG, but without the increased sodium content. As a soluble source of calcium ions, this chemical is also used as a first-aid treatment for exposure to hydrofluoric acid. ",'flavour enhancer','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E622','Monopotassium glutamate','Vegan',"Monopotassium glutamate (MPG) is a compound with formula KC5H8NO4. It is a potassium acid salt of glutamic acid. ",'flavour enhancer','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E621','Monosodium glutamate (MSG)','Vegan',"Glutamic acid (symbol Glu or E) is an α-amino acid that is used by almost all living beings in the biosynthesis of proteins. It is non-essential in humans, meaning the body can synthesize it. It is also an excitatory neurotransmitter, in fact the most abundant one, in the vertebrate nervous system. It serves as the precursor for the synthesis of the inhibitory gamma-aminobutyric acid (GABA) in GABA-ergic neurons. ",'flavour enhancer','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E620','Glutamic acid','Vegan',"Glutamic acid (symbol Glu or E) is an α-amino acid that is used by almost all living beings in the biosynthesis of proteins. It is non-essential in humans, meaning the body can synthesize it. It is also an excitatory neurotransmitter, in fact the most abundant one, in the vertebrate nervous system. It serves as the precursor for the synthesis of the inhibitory gamma-aminobutyric acid (GABA) in GABA-ergic neurons. ",'flavour enhancer','Approved in the EU.']);

    //Glazing
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E999','Quillaia extract','Vegan',"Quillaia is the milled inner bark or small stems and branches of the soapbark (Quillaja saponaria, Molina). Other names include Murillo bark extract, Panama bark extract, Vanilla extract, Quillaia extract, Quillay bark extract, and Soapbark extract. It contains a high concentration of saponins which is increased by processing; highly purified quillaia is used to enhance vaccines. Other compounds in the extract include tannins and other polyphenols, and calcium oxalate. It is listed as an ingredient in root beer and cream soda. ",'foaming agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E969','Advantame','May or may not be vegan',"Advantame is a non-caloric artificial sweetener and aspartame analog by Ajinomoto. By mass, it is about 20 000 times sweeter than sucrose and about 110 times sweeter than aspartame. It has no notable off-flavors when compared to sucrose and tastes sweet a bit longer than aspartame and is chemically more stable. It can be blended with many other natural and artificial sweeteners. ",'sweetener','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E968','Erythritol','May or may not be vegan',"Erythritol is a chemical compound, a sugar alcohol (or polyol), used as a food additive and sugar substitute. Its formula is C4H10O4, or HO(CH2)(CHOH)2(CH2)OH; specifically, one particular stereoisomer with that formula. ",'sweetener','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E967','Xylitol','Vegan',"Xylitol is a chemical compound with the formula C5H12O5, or HO(CH2)(CHOH)3(CH2)OH; specifically, one particular stereoisomer with that structural formula. It is a colorless or white crystalline solid that is soluble in water. It can be classified as a polyalcohol and a sugar alcohol, specifically an alditol. The name derives from Ancient Greek: ξύλον, xyl[on], \"wood\", with the suffix -itol used to denote sugar alcohols. ",'sweetener','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E966','Lactitol','Not Vegan',"Lactitol is a sugar alcohol used as a replacement bulk sweetener for low calorie foods with approximately 40% of the sweetness of sugar. It is also used medically as a laxative. Lactitol is produced by two manufacturers, Danisco and Purac Biochem. ",'sweetener','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E965','Maltitol (i) Maltitol (ii) Maltitol syrup (sweetener) (stabiliser)','Vegan',"Maltitol is a sugar alcohol (a polyol) used as a sugar substitute. It has 75–90% of the sweetness of sucrose (table sugar) and nearly identical properties, except for browning. It is used to replace table sugar because it is half as energetic, does not promote tooth decay, and has a somewhat lesser effect on blood glucose. In chemical terms, maltitol is known as 4-O-α-glucopyranosyl-D-sorbitol. It is used in commercial products under trade names such as Lesys, Maltisweet and SweetPearl. ",'humectant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E962','Aspartame-acesulfame salt (sweetener)','May or may not be vegan',"Aspartame-acesulfame salt is an artificial sweetener marketed under the name Twinsweet. It is produced by soaking a 2-1 mixture of aspartame and acesulfame potassium in an acidic solution and allowing it to crystallize; moisture and potassium are removed during this process. It is approximately 350 times as sweet as sucrose. It has been given the E number E962. ",'stabiliser','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E961','Neotame','May or may not be vegan',"Neotame, also known by the trade name Newtame, is a non-caloric artificial sweetener and aspartame analog by NutraSweet. By mass, it is 8000 times sweeter than sucrose. It has no notable off-flavors when compared to sucrose. It enhances original food flavors. It can be used alone, but is often mixed with other sweeteners to increase their individual sweetness (i.e. synergistic effect) and decrease their off-flavors (e.g. saccharin). It is chemically somewhat more stable than aspartame. Its use can be cost effective in comparison to other sweeteners as smaller amounts of neotame are needed. ",'sweetener','Approved in 2010']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E960','Steviol glycosides','May or may not be vegan',"Steviol glycosides are the chemical compounds responsible for the sweet taste of the leaves of the South American plant Stevia rebaudiana (Asteraceae) and the main ingredients (or precursors) of many sweeteners marketed under the generic name stevia and several trade names. They also occur in the related species Stevia phlebophylla (but in no other species of Stevia) and in the plant Rubus chingii (Rosaceae). ",'sweetener','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E959','Neohesperidine dihydrochalcone (sweetener)','Vegan',"Neohesperidin dihydrochalcone, sometimes abbreviated to neohesperidin DC or simply NHDC, is an artificial sweetener derived from citrus. ",'flavour enhancer','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E958','Glycyrrhizin (sweetener)','May or may not be vegan',"Glycyrrhizin (or glycyrrhizic acid or glycyrrhizinic acid) is the chief sweet-tasting constituent of Glycyrrhiza glabra (liquorice) root. Structurally, it is a saponin used as an emulsifier and gel-forming agent in foodstuffs and cosmetics. Its aglycone is enoxolone assessed as a prodrug used in Japan to reduce the risk of liver cancer in people with chronic hepatitis C. ",'flavour enhancer','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E957','Thaumatin (sweetener)','Vegan',"Thaumatin (also known as talin) is a low-calorie sweetener and flavour modifier. The protein is often used primarily for its flavour-modifying properties and not exclusively as a sweetener. ",'flavour enhancer','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E956','Alitame','May or may not be vegan',"Alitame is an aspartic acid-containing dipeptide sweetener. It was developed by Pfizer in the early 1980s and currently marketed in some countries under the brand name Aclame. Most dipeptides are not sweet, but the unexpected discovery of aspartame in 1965 led to a search for similar compounds that shared its sweetness. Alitame is one such second-generation dipeptide sweetener. Neotame, developed by the owners of the NutraSweet brand, is another. ",'sweetener','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E955','Sucralose (Trichlorogalactosucrose)','Vegan',"Sucralose is an artificial sweetener and sugar substitute. The majority of ingested sucralose is not broken down by the body, so it is noncaloric. In the European Union, it is also known under the E number E955. It is produced by chlorination of sucrose. Sucralose is about 320 to 1,000 times sweeter than sucrose, three times as sweet as both aspartame and acesulfame potassium, and twice as sweet as sodium saccharin. Evidence of benefit is lacking for long-term weight loss with some data supporting weight gain and heart disease risks. ",'sweetener','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E954','Saccharin and its sodium, potassium and calcium salts','Vegan',"Sodium saccharin (benzoic sulfimide) is an artificial sweetener with effectively no food energy. It is about 300–400 times as sweet as sucrose but has a bitter or metallic aftertaste, especially at high concentrations. Saccharin is used to sweeten products such as drinks, candies, cookies, and medicines. ",'sweetener','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E953','Isomalt, Isomaltitol','Vegan',"",'sweetener','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E952','Cyclamic acid and its sodium and calcium salts, also known as Cyclamate','Vegan',"",'sweetener','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E951','Aspartame','Vegan',"",'sweetener','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E950','Acesulfame potassium','Vegan',"",'sweetener','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E949','Hydrogen','Vegan',"",'packaging gas','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E948','Oxygen','Vegan',"",'packaging gas','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E946','Octafluorocyclobutane','May or may not be vegan',"Octafluorocyclobutane, or perfluorocyclobutane, C4F8, is an organofluorine compound which enjoys several niche applications. It is related to cyclobutane by replacement of all C–H bonds with C–F bonds. Octafluorocyclobutane is produced by the dimerization of tetrafluoroethylene and the reductive coupling of 1,2-dichloro-1,1,2,2-tetrafluoroethane. ",'propellant','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E945','Chloropentafluoroethane','May or may not be vegan',"Propane (/ˈproʊpeɪn/) is a three-carbon alkane with the molecular formula C3H8. It is a gas at standard temperature and pressure, but compressible to a transportable liquid. A by-product of natural gas processing and petroleum refining, it is commonly used as a fuel. Discovered in 1857 by the French chemist Marcellin Berthelot, it became commercially available in the US by 1911. Propane is one of a group of liquefied petroleum gases (LP gases). The others include butane, propylene, butadiene, butylene, isobutylene, and mixtures thereof. ",'propellant','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E944','Propane','Vegan',"Propane (/ˈproʊpeɪn/) is a three-carbon alkane with the molecular formula C3H8. It is a gas at standard temperature and pressure, but compressible to a transportable liquid. A by-product of natural gas processing and petroleum refining, it is commonly used as a fuel. Discovered in 1857 by the French chemist Marcellin Berthelot, it became commercially available in the US by 1911. Propane is one of a group of liquefied petroleum gases (LP gases). The others include butane, propylene, butadiene, butylene, isobutylene, and mixtures thereof. ",'propellant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E943b','Isobutane','Vegan',"Isobutane, also known as i-butane, 2-methylpropane or methylpropane, is a chemical compound with molecular formula HC(CH3)3. It is an isomer of butane. It is the simplest alkane with a tertiary carbon. Isobutane is used as a precursor molecule in the petrochemical industry, for example in the synthesis of isooctane. ",'propellant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E943a','Butane','Vegan',"",'propellant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E942','Nitrous oxide','Vegan',"",'propellant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E941','Nitrogen (packaging gas)','Vegan',"",'propellant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E940','Dichlorodifluoromethane','Vegan',"Dichlorodifluoromethane (R-12) is a colorless gas usually sold under the brand name Freon-12, and a chlorofluorocarbon halomethane (CFC) used as a refrigerant and aerosol spray propellant. Complying with the Montreal Protocol, its manufacture was banned in developed countries (non-article 5 countries) in 1996, and developing countries (article 5 countries) in 2010 due to concerns about its damaging effect on the ozone layer.[better source needed] Its only allowed usage is as fire retardant in submarines and aircraft. It is soluble in many organic solvents. Dichlorodifluoromethane was one of the original propellants for Silly String. R-12 cylinders are colored white. ",'packaging gas','Banned in all countries, in compliance with the Montreal Protocol.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E939','Helium','Vegan',"Helium (from Greek: ἥλιος, romanized: Helios, lit. 'Sun') is a chemical element with the symbol He and atomic number 2. It is a colorless, odorless, tasteless, non-toxic, inert, monatomic gas, the first in the noble gas group in the periodic table. Its boiling point is the lowest among all the elements. Helium is the second lightest and second most abundant element in the observable universe (hydrogen is the lightest and most abundant). It is present at about 24% of the total elemental mass, which is more than 12 times the mass of all the heavier elements combined. Its abundance is similar to this in both the Sun and in Jupiter. This is due to the very high nuclear binding energy (per nucleon) of helium-4, with respect to the next three elements after helium. This helium-4 binding energy also accounts for why it is a product of both nuclear fusion and radioactive decay. Most helium in the universe is helium-4, the vast majority of which was formed during the Big Bang. Large amounts of new helium are being created by nuclear fusion of hydrogen in stars. ",'packaging gas','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E938','Argon','Vegan',"Argon is a chemical element with the symbol Ar and atomic number 18. It is in group 18 of the periodic table and is a noble gas. Argon is the third-most abundant gas in the Earth's atmosphere, at 0.934% (9340 ppmv). It is more than twice as abundant as water vapor (which averages about 4000 ppmv, but varies greatly), 23 times as abundant as carbon dioxide (400 ppmv), and more than 500 times as abundant as neon (18 ppmv). Argon is the most abundant noble gas in Earth's crust, comprising 0.00015% of the crust. ",'packaging gas','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E930','Calcium peroxide (improving agent)','Vegan',"Calcium peroxide or calcium dioxide is the inorganic compound with the formula CaO2. It is the peroxide (O22−) salt of Ca2+. Commercial samples can be yellowish, but the pure compound is white. It is almost insoluble in water. ",'bleach','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E929','Acetone peroxide','May or may not be vegan',"Chlorine dioxide is a chemical compound with the formula ClO2. This yellowish-green gas crystallizes as bright orange crystals at −59 °C. As one of several oxides of chlorine, it is a potent and useful oxidizing agent used in water treatment and in bleaching. The compound has been fraudulently marketed as a cure for a wide range of diseases, including childhood autism. Children who have been given enemas of chlorine dioxide as supposed cure for childhood autism have suffered life-threatening ailments and even died. The FDA has stated that chlorine dioxide has no health benefits and should not be ingested for any reason. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E928','Benzoyl peroxide (improving agent)','Vegan',"Chlorine dioxide is a chemical compound with the formula ClO2. This yellowish-green gas crystallizes as bright orange crystals at −59 °C. As one of several oxides of chlorine, it is a potent and useful oxidizing agent used in water treatment and in bleaching. The compound has been fraudulently marketed as a cure for a wide range of diseases, including childhood autism. Children who have been given enemas of chlorine dioxide as supposed cure for childhood autism have suffered life-threatening ailments and even died. The FDA has stated that chlorine dioxide has no health benefits and should not be ingested for any reason. ",'bleach','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E927b','Carbamide (urea)','May or may not be vegan',"Chlorine dioxide is a chemical compound with the formula ClO2. This yellowish-green gas crystallizes as bright orange crystals at −59 °C. As one of several oxides of chlorine, it is a potent and useful oxidizing agent used in water treatment and in bleaching. The compound has been fraudulently marketed as a cure for a wide range of diseases, including childhood autism. Children who have been given enemas of chlorine dioxide as supposed cure for childhood autism have suffered life-threatening ailments and even died. The FDA has stated that chlorine dioxide has no health benefits and should not be ingested for any reason. ",'improving agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E927a','Azodicarbonamide','May or may not be vegan',"Chlorine dioxide is a chemical compound with the formula ClO2. This yellowish-green gas crystallizes as bright orange crystals at −59 °C. As one of several oxides of chlorine, it is a potent and useful oxidizing agent used in water treatment and in bleaching. The compound has been fraudulently marketed as a cure for a wide range of diseases, including childhood autism. Children who have been given enemas of chlorine dioxide as supposed cure for childhood autism have suffered life-threatening ailments and even died. The FDA has stated that chlorine dioxide has no health benefits and should not be ingested for any reason. ",'improving agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E926','Chlorine dioxide (preservative)','Vegan',"Chlorine dioxide is a chemical compound with the formula ClO2. This yellowish-green gas crystallizes as bright orange crystals at −59 °C. As one of several oxides of chlorine, it is a potent and useful oxidizing agent used in water treatment and in bleaching. The compound has been fraudulently marketed as a cure for a wide range of diseases, including childhood autism. Children who have been given enemas of chlorine dioxide as supposed cure for childhood autism have suffered life-threatening ailments and even died. The FDA has stated that chlorine dioxide has no health benefits and should not be ingested for any reason. ",'bleach','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E925','Chlorine','Vegan',"Chlorine is a chemical element with the symbol Cl and atomic number 17. The second-lightest of the halogens, it appears between fluorine and bromine in the periodic table and its properties are mostly intermediate between them. Chlorine is a yellow-green gas at room temperature. It is an extremely reactive element and a strong oxidising agent: among the elements, it has the highest electron affinity and the third-highest electronegativity on the Pauling scale, behind only oxygen and fluorine. ",'preservative, bleach, improving agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E924b','Calcium bromate','May or may not be vegan',"Calcium bromate, Ca(BrO3)2, is a calcium salt of bromic acid. It is most commonly encountered as the monohydrate, Ca(BrO3)2•H2O. ",'improving agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E924','Potassium bromate','Vegan',"Potassium bromate (KBrO3), is a bromate of potassium and takes the form of white crystals or powder. It is a strong oxidizing agent. ",'improving agent','Banned in the EU']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E923','Ammonium persulfate','Vegan',"Ammonium persulfate (APS) is the inorganic compound with the formula (NH4)2S2O8. It is a colourless (white) salt that is highly soluble in water, much more so than the related potassium salt. It is a strong oxidizing agent that is used in polymer chemistry, as an etchant, and as a cleaning and bleaching agent. ",'improving agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E922','Potassium persulfate','Vegan',"Potassium persulfate is the inorganic compound with the formula K2S2O8. Also known as potassium peroxydisulfate or KPS, it is a white solid that is sparingly soluble in cold water, but dissolves better in warm water. This salt is a powerful oxidant, commonly used to initiate polymerizations. ",'improving agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E921','L-cystine','Not Vegan',"Cystine is the oxidized dimer form of the amino acid cysteine and has the formula (SCH2CH(NH2)CO2H)2. It is a white solid that is slightly soluble in water. It serves two biological functions: a site of redox reactions and a mechanical linkage that allows proteins to retain their three-dimensional structure. ",'improving agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E920','L-cysteine','Not Vegan',"Cysteine (symbol Cys or C; /ˈsɪstiiːn/) is a semiessential proteinogenic amino acid with the formula HO2CCH(NH2)CH2SH. The thiol side chain in cysteine often participates in enzymatic reactions, as a nucleophile. The thiol is susceptible to oxidation to give the disulfide derivative cystine, which serves an important structural role in many proteins. When used as a food additive, it has the E number E920. It is encoded by the codons UGU and UGC. ",'improving agent','Banned in the EU']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E919','Nitrosyl chloride','May or may not be vegan',"Nitrosyl chloride is the chemical compound with the formula NOCl. It is a yellow gas that is most commonly encountered as a decomposition product of aqua regia, a mixture of hydrochloric acid and nitric acid. It is a strong electrophile and oxidizing agent. It is sometimes called Tilden's reagent. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E918','Nitrogen oxides','May or may not be vegan',"Nitrogen oxide may refer to a binary compound of oxygen and nitrogen, or a mixture of such compounds: ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E917','Potassium iodate','May or may not be vegan',"Potassium iodate (KIO3) is a chemical compound. It is ionic, made up of K+ ions and IO3− ions in a 1:1 ratio. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E916','Calcium iodate','May or may not be vegan',"Calcium iodates are inorganic compound composed of calcium and iodate anion. Two forms are known, anhydrous Ca(IO3)2 and the hexahydrate Ca(IO3)2(H2O). Both are colourless salts that occur naturally as the minerals called lautarite and bruggenite, respectively. A third mineral form of calcium iodate is dietzeite, a salt containing chromate with the formula Ca2(IO3)2CrO4. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E915','Esters of colophony','Vegan',"Rosin, also called colophony or Greek pitch (Latin: pix græca), is a solid form of resin obtained from pines and some other plants, mostly conifers, produced by heating fresh liquid resin to vaporize the volatile liquid terpene components. It is semi-transparent and varies in color from yellow to black. At room temperature rosin is brittle, but it melts at stove-top temperature. It chiefly consists of various resin acids, especially abietic acid. The term \"colophony\" comes from colophonia resina, Latin for \"resin from Colophon\", an ancient Ionic city. ",'glazing agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E914','Oxidized polyethylene wax, oxidized polyethylene','Vegan',"Cysteine (symbol Cys or C; /ˈsɪstiiːn/) is a semiessential proteinogenic amino acid with the formula HO2CCH(NH2)CH2SH. The thiol side chain in cysteine often participates in enzymatic reactions, as a nucleophile. The thiol is susceptible to oxidation to give the disulfide derivative cystine, which serves an important structural role in many proteins. When used as a food additive, it has the E number E920. It is encoded by the codons UGU and UGC. ",'glazing agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E913','Lanolin, sheep wool grease','Not Vegan',"Cysteine (symbol Cys or C; /ˈsɪstiiːn/) is a semiessential proteinogenic amino acid with the formula HO2CCH(NH2)CH2SH. The thiol side chain in cysteine often participates in enzymatic reactions, as a nucleophile. The thiol is susceptible to oxidation to give the disulfide derivative cystine, which serves an important structural role in many proteins. When used as a food additive, it has the E number E920. It is encoded by the codons UGU and UGC. ",'glazing agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E912','Montanic acid esters, Montan acid esters','Vegan',"Cysteine (symbol Cys or C; /ˈsɪstiiːn/) is a semiessential proteinogenic amino acid with the formula HO2CCH(NH2)CH2SH. The thiol side chain in cysteine often participates in enzymatic reactions, as a nucleophile. The thiol is susceptible to oxidation to give the disulfide derivative cystine, which serves an important structural role in many proteins. When used as a food additive, it has the E number E920. It is encoded by the codons UGU and UGC. ",'glazing agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E911','Methyl esters of fatty acids','May or may not be vegan',"Cysteine (symbol Cys or C; /ˈsɪstiiːn/) is a semiessential proteinogenic amino acid with the formula HO2CCH(NH2)CH2SH. The thiol side chain in cysteine often participates in enzymatic reactions, as a nucleophile. The thiol is susceptible to oxidation to give the disulfide derivative cystine, which serves an important structural role in many proteins. When used as a food additive, it has the E number E920. It is encoded by the codons UGU and UGC. ",'glazing agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E910','Wax esters','Not Vegan',"Cysteine (symbol Cys or C; /ˈsɪstiiːn/) is a semiessential proteinogenic amino acid with the formula HO2CCH(NH2)CH2SH. The thiol side chain in cysteine often participates in enzymatic reactions, as a nucleophile. The thiol is susceptible to oxidation to give the disulfide derivative cystine, which serves an important structural role in many proteins. When used as a food additive, it has the E number E920. It is encoded by the codons UGU and UGC. ",'glazing agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E909','Spermaceti wax','May or may not be vegan',"Spermaceti (from Greek sperma meaning \"seed\", and ceti, the genitive form of \"whale\") is a waxy substance found in the head cavities of the sperm whale (and, in smaller quantities, in the oils of other whales). Spermaceti is created in the spermaceti organ inside the whale's head. This organ may contain as much as 1,900 litres (500 US gal) of spermaceti. ",'glazing agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E908','Rice bran wax','Vegan',"Rice bran wax is the vegetable wax extracted from the bran oil of rice (Oryza sativa). ",'glazing agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E907','Crystalline wax','Vegan',"Hydrogenated poly-1-decene is a colourless glazing agent. It is \"a mixture of isoparaffinic molecules of known structure, prepared by hydrogenation of mixtures of tri-, tetra- penta- and hexa-1-decenes\". It was reviewed in 2001 by the Scientific Committee on Food of the DG Health. It was \"proposed as a substitute for white mineral oil. The food additive applications include those of glazing agent for confectionery and dried fruit, and processing aid uses as a lubricant and release agent, especially in bread baking using tins. It has been permitted for use in Finland, and a “Case of Need” has been accepted in the United Kingdom.\" The substance is a mix of inert saturated hydrocarbons, which are not easily metabolised. ",'glazing agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E906','Gum benzoic','Vegan',"Benzoin /ˈbɛnzoʊ.ɪn/ or benjamin (corrupted pronunciation) is a balsamic resin obtained from the bark of several species of trees in the genus Styrax. It is used in perfumes, some kinds of incense, as a flavoring, and medicine (see tincture of benzoin). It is distinct from the chemical compound benzoin, which is ultimately derived from benzoin resin; the resin, however, does not contain this compound. ",'flavour enhancer','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E905c','Petroleum wax (i)Microcrystalline wax (ii) Paraffin wax','Vegan',"Microcrystalline waxes are a type of wax produced by de-oiling petrolatum, as part of the petroleum refining process. In contrast to the more familiar paraffin wax which contains mostly unbranched alkanes, microcrystalline wax contains a higher percentage of isoparaffinic (branched) hydrocarbons and naphthenic hydrocarbons. It is characterized by the fineness of its crystals in contrast to the larger crystal of paraffin wax. It consists of high molecular weight saturated aliphatic hydrocarbons. It is generally darker, more viscous, denser, tackier and more elastic than paraffin waxes, and has a higher molecular weight and melting point. The elastic and adhesive characteristics of microcrystalline waxes are related to the non-straight chain components which they contain. Typical microcrystalline wax crystal structure is small and thin, making them more flexible than paraffin wax. It is commonly used in cosmetic formulations. ",'glazing agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E905b','Petrolatum','Vegan',"Petroleum jelly, petrolatum, white petrolatum, soft paraffin, or multi-hydrocarbon, CAS number 8009-03-8, is a semi-solid hydrocarbon C15H15N, chemical name 1,1,2-Trimethylbenzeindole, originally promoted as a topical ointment for its healing properties. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E905a','Mineral oil','Vegan',"Mineral oil is any of various colorless, odorless, light mixtures of higher alkanes from a mineral source, particularly a distillate of petroleum, as distinct from usually edible vegetable oils. ",'anti-foaming agent','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E905','Paraffins','Vegan',"Paraffin wax (or petroleum wax) is a soft colorless solid, derived from petroleum, coal or shale oil, that consists of a mixture of hydrocarbon molecules containing between twenty and forty carbon atoms. It is solid at room temperature and begins to melt above approximately 37 °C (99 °F), and its boiling point is above 370 °C (698 °F). Common applications for paraffin wax include lubrication, electrical insulation, and candles;  dyed paraffin wax can be made into crayons. It is distinct from kerosene and other petroleum products that are sometimes called paraffin. ",'','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E904','Shellac','Not Vegan',"Shellac is a resin secreted by the female lac bug on trees in the forests of India and Thailand. It is processed and sold as dry flakes (pictured) and dissolved in alcohol to make liquid shellac, which is used as a brush-on colorant, food glaze and wood finish. Shellac functions as a tough natural primer, sanding sealant, tannin-blocker, odour-blocker, stain, and high-gloss varnish. Shellac was once used in electrical applications as it possesses good insulation qualities and it seals out moisture. Phonograph and 78 rpm gramophone records were made of it until they were replaced by vinyl long-playing records from the 1950s onwards. ",'glazing agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E903','Carnauba wax','Vegan',"Carnauba (/kɑːrˈnɔːbə, -naʊ-/; Portuguese: carnaúba [kahnaˈubɐ]), also called Brazil wax and palm wax, is a wax of the leaves of the palm Copernicia prunifera (Synonym: Copernicia cerifera), a plant native to and grown only in the northeastern Brazilian states of Piauí, Ceará, Maranhão, Bahia, and Rio Grande do Norte. It is known as \"queen of waxes\" and in its pure state, usually comes in the form of hard yellow-brown flakes. It is obtained from the leaves of the carnauba palm by collecting and drying them, beating them to loosen the wax, then refining and bleaching the wax. As a food additive, its E number is E903. ",'glazing agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E902','Candelilla wax','Vegan',"Candelilla wax is a wax derived from the leaves of the small Candelilla shrub native to northern Mexico and the southwestern United States, Euphorbia cerifera and Euphorbia antisyphilitica, from the family Euphorbiaceae. It is yellowish-brown, hard, brittle, aromatic, and opaque to translucent. ",'glazing agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E901','Beeswax, white and yellow','Not Vegan',"Beeswax (cera alba) is a natural wax produced by honey bees of the genus Apis. The wax is formed into scales by eight wax-producing glands in the abdominal segments of worker bees, which discard it in or at the hive. The hive workers collect and use it to form cells for honey storage and larval and pupal protection within the beehive. Chemically, beeswax consists mainly of esters of fatty acids and various long-chain alcohols. ",'glazing agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E900','Dimethyl polysiloxane (anti-foaming agent)','Vegan',"",'anti-caking agent','Approved in the EU.']);

    //Preservatives
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E297','Fumaric acid','Vegan',"Fumaric acid or trans-butenedioic acid is a chemical compound with the formula HO2CCH=CHCO2H. Fumaric acid has a fruit-like taste and has been used as a food acidulant since 1946. Its E number is E297. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E296','Malic acid (acid)','Vegan',"Malic acid is an organic compound with the molecular formula C4H6O5. It is a dicarboxylic acid that is made by all living organisms, contributes to the sour taste of fruits, and is used as a food additive. Malic acid has two stereoisomeric forms (L- and D-enantiomers), though only the L-isomer exists naturally. The salts and esters of malic acid are known as malates. The malate anion is an intermediate in the citric acid cycle. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E290','Carbon dioxide','Vegan',"",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E285','Sodium tetraborate (borax)','Vegan',"Boric acid, also called hydrogen borate, boracic acid, and orthoboric acid is a weak, monobasic Lewis acid of boron. However, some of its behaviour towards some chemical reactions suggest it to be tribasic acid in Bronsted sense as well. Boric acid is often used as an antiseptic, insecticide, flame retardant, neutron absorber, or precursor to other chemical compounds. It has the chemical formula H3BO3 (sometimes written B(OH)3), and exists in the form of colorless crystals or a white powder that dissolves in water. When occurring as a mineral, it is called sassolite. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E284','Boric acid','Vegan',"Boric acid, also called hydrogen borate, boracic acid, and orthoboric acid is a weak, monobasic Lewis acid of boron. However, some of its behaviour towards some chemical reactions suggest it to be tribasic acid in Bronsted sense as well. Boric acid is often used as an antiseptic, insecticide, flame retardant, neutron absorber, or precursor to other chemical compounds. It has the chemical formula H3BO3 (sometimes written B(OH)3), and exists in the form of colorless crystals or a white powder that dissolves in water. When occurring as a mineral, it is called sassolite. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E283','Potassium propionate','Vegan',"Potassium propanoate or potassium propionate has formula K(C2H5COO). Its melting point is 410 °C. It is the potassium salt of propanoic acid. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E282','Calcium propionate','Vegan',"Calcium propanoate or calcium propionate has the formula Ca(C2H5COO)2. It is the calcium salt of propanoic acid. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E281','Sodium propionate','Vegan',"Sodium propanoate or sodium propionate is the sodium salt of propionic acid which has the chemical formula Na(C2H5COO). This white crystalline solid is deliquescent in moist air. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E280','Propionic acid','Vegan',"Propionic acid (/proʊpiˈɒnɪk/, from the Greek words protos, meaning \"first\", and pion, meaning \"fat\"; also known as propanoic acid) is a naturally occurring carboxylic acid with chemical formula CH3CH2CO2H. It is a liquid with a pungent and unpleasant smell somewhat resembling body odor. The anion CH3CH2CO2− as well as the salts and esters of propionic acid are known as propionates or propanoates. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E270','Lactic acid (preservative)','May or may not be vegan',"Lactic acid is an organic acid. It has a molecular formula CH3CH(OH)COOH. It is white in solid state and it is miscible with water. While in liquid state (dissolved state) it is a colorless solution. Production includes both artificial synthesis as well as natural sources. Lactic acid is an alpha-hydroxy acid (AHA) due to the presence of carboxyl group adjacent to the hydroxyl group. It is used as a synthetic intermediate in many organic synthesis industries and in various biochemical industries. The conjugate base of lactic acid is called lactate. ",'antioxidant','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E266','Sodium dehydroacetate','May or may not be vegan',"Sodium dehydroacetate is a compound with the formula Na(CH3C5HO(O2)(CH3)CO). It is the sodium salt of dehydroacetic acid. ",'preservative','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E265','Dehydroacetic acid','May or may not be vegan',"Potassium nitrate is a chemical compound with the chemical formula KNO3. It is an ionic salt of potassium ions K+ and nitrate ions NO3−, and is therefore an alkali metal nitrate. ",'preservative','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E264','Ammonium acetate','Vegan',"Potassium nitrate is a chemical compound with the chemical formula KNO3. It is an ionic salt of potassium ions K+ and nitrate ions NO3−, and is therefore an alkali metal nitrate. ",'preservative','Approved in Australia and New Zealand']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E263','Calcium acetate (preservative)','Vegan',"Potassium nitrate is a chemical compound with the chemical formula KNO3. It is an ionic salt of potassium ions K+ and nitrate ions NO3−, and is therefore an alkali metal nitrate. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E262','Sodium acetates (i) Sodium acetate (ii) Sodium diacetate (sodium hydrogen acetate)','Vegan',"Potassium nitrate is a chemical compound with the chemical formula KNO3. It is an ionic salt of potassium ions K+ and nitrate ions NO3−, and is therefore an alkali metal nitrate. ",'preservative, acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E261','Potassium acetate (preservative)','Vegan',"Potassium nitrate is a chemical compound with the chemical formula KNO3. It is an ionic salt of potassium ions K+ and nitrate ions NO3−, and is therefore an alkali metal nitrate. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E260','Acetic acid (preservative)','Vegan',"Potassium nitrate is a chemical compound with the chemical formula KNO3. It is an ionic salt of potassium ions K+ and nitrate ions NO3−, and is therefore an alkali metal nitrate. ",'acidity regulator','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E252','Potassium nitrate (Saltpetre)','May or may not be vegan',"Potassium nitrate is a chemical compound with the chemical formula KNO3. It is an ionic salt of potassium ions K+ and nitrate ions NO3−, and is therefore an alkali metal nitrate. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E251','Sodium nitrate (Chile saltpetre)','Vegan',"Sodium nitrate is the chemical compound with the formula NaNO3. This alkali metal nitrate salt is also known as Chile saltpetre (large deposits of which were historically mined in Chile) to distinguish it from ordinary saltpetre, potassium nitrate. The mineral form is also known as nitratine, nitratite or soda niter. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E250','Sodium nitrite','Vegan',"Potassium nitrite (distinct from potassium nitrate) is the inorganic compound with the chemical formula KNO2. It is an ionic salt of potassium ions K+ and nitrite ions NO2−, which forms a white or slightly yellow, hygroscopic crystalline powder that is soluble in water. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E249','Potassium nitrite','Vegan',"Potassium nitrite (distinct from potassium nitrate) is the inorganic compound with the chemical formula KNO2. It is an ionic salt of potassium ions K+ and nitrite ions NO2−, which forms a white or slightly yellow, hygroscopic crystalline powder that is soluble in water. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E242','Dimethyl dicarbonate','Vegan',"Dimethyl dicarbonate (DMDC) is an organic compound which is a colorless liquid with a pungent odor at high concentration at room temperature. It is primarily used as a beverage preservative, processing aid, or sterilant (INS No. 242), and acts by inhibiting the enzymes acetate kinase and L-glutamic acid decarboxylase. It has also been proposed that DMDC inhibits the enzymes alcohol dehydrogenase and glyceraldehyde 3-phosphate dehydrogenase by causing the methoxycarbonylation of their histidine components. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E240','Formaldehyde','Vegan',"Formaldehyde (systematic name methanal) is a naturally occurring organic compound with the formula CH2O (H−CHO). It is the simplest of the aldehydes (R−CHO). The common name of this substance comes from its similarity and relation to formic acid. ",'preservative','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E239','Hexamine (hexamethylene tetramine)','Vegan',"Hexamethylenetetramine or methenamine, also known as hexamine or urotropin, is a heterocyclic organic compound with the formula (CH2)6N4. This white crystalline compound is highly soluble in water and polar organic solvents. It has a cage-like structure similar to adamantane. It is useful in the synthesis of other chemical compounds, e.g., plastics, pharmaceuticals, rubber additives. It sublimes in vacuum at 280 °C. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E238','Calcium formate','Vegan',"Calcium formate is the calcium salt of formic acid. It is also known as E238. Under this E number it is used as an animal feed preservative within EU, but not in foods intended for people. ",'preservative','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E237','Sodium formate','Vegan',"Sodium formate, HCOONa, is the sodium salt of formic acid, HCOOH. It usually appears as a white deliquescent powder. ",'preservative','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E236','Formic acid','Vegan',"Formic acid, systematically named methanoic acid, is the simplest carboxylic acid, and has the chemical formula HCOOH. The chemical composition is HCOOH. It is an important intermediate in chemical synthesis and occurs naturally, most notably in some ants. The word \"formic\" comes from the Latin word for ant, formica, referring to its early isolation by the distillation of ant bodies. Esters, salts, and the anion derived from formic acid are called formates. Industrially, formic acid is produced from methanol. ",'preservative','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E235','Natamycin, Pimaracin','Vegan',"Tiabendazole (INN, BAN), also known as thiabendazole (AAN, USAN) or TBZ and the trade names Mintezol, Tresaderm, and Arbotect, is a preservative, an antifungal agent, and an antiparasitic agent. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E234','Nisin','Vegan',"Tiabendazole (INN, BAN), also known as thiabendazole (AAN, USAN) or TBZ and the trade names Mintezol, Tresaderm, and Arbotect, is a preservative, an antifungal agent, and an antiparasitic agent. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E233','Thiabendazole','Vegan',"Tiabendazole (INN, BAN), also known as thiabendazole (AAN, USAN) or TBZ and the trade names Mintezol, Tresaderm, and Arbotect, is a preservative, an antifungal agent, and an antiparasitic agent. ",'preservative','Not approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E232','Sodium orthophenyl phenol','Vegan',"Sodium orthophenyl phenol is a compound used as a disinfectant. It is the sodium salt of 2-phenylphenol. ",'preservative','Not approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E231','Orthophenyl phenol','Vegan',"Biphenyl (also known as diphenyl, phenylbenzene, 1,1′-biphenyl, lemonene or BP) is an organic compound that forms colorless crystals. Particularly in older literature, compounds containing the functional group consisting of biphenyl less one hydrogen (the site at which it is attached) may use the prefixes xenyl or diphenylyl. ",'preservative','Not approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E230','Biphenyl, diphenyl','Vegan',"Biphenyl (also known as diphenyl, phenylbenzene, 1,1′-biphenyl, lemonene or BP) is an organic compound that forms colorless crystals. Particularly in older literature, compounds containing the functional group consisting of biphenyl less one hydrogen (the site at which it is attached) may use the prefixes xenyl or diphenylyl. ",'preservative','Not approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E228','Potassium hydrogen sulfite','Vegan',"Potassium hydrogen sulfite or potassium bisulfite is a chemical compound with the chemical formula KHSO3. It is used during the production of alcoholic beverages as a sterilising agent. This additive is classified as E number E228 under the current EU approved food additive. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E227','Calcium hydrogen sulfite (preservative)','Vegan',"Calcium bisulfite (calcium bisulphite) is an inorganic compound which is the salt of a calcium cation and a bisulfite anion. It may be prepared by reacting lime with an excess of sulfurous acid, essentially a mixture of sulfur dioxide and water. It is a weak reducing agent, as is sulfur dioxide, sulfites, and any other compound containing sulfur in the +4 oxidation state. As a food additive it is used as a preservative under the E number E227. Calcium bisulfite is an acid salt and behaves like an acid in aqueous solution. ",'firming agent','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E226','Calcium sulfite','Vegan',"Calcium sulfite, or calcium sulphite, is a chemical compound, the calcium salt of sulfite with the formula CaSO3·x(H2O). Two crystalline forms are known, the hemihydrate and the tetrahydrate, respectively CaSO3·½(H2O) and CaSO3·4(H2O). All forms are white solids. It is most notable as the product of flue-gas desulfurization. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E225','Potassium sulfite','Vegan',"Potassium sulfite (K2SO3) is a chemical compound which is the salt of potassium cation and sulfite anion. As a food additive it is used as a preservative under the E number E225 (INS number 225). It is approved for use in Australia and New Zealand and is not approved in the EU or US. ",'preservative','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E224','Potassium metabisulfite','Vegan',"Potassium metabisulfite, K2S2O5, also known as potassium pyrosulfite, is a white crystalline powder with a pungent sulfur odour. The main use for the chemical is as an antioxidant or chemical sterilant. It is a disulfite and is chemically very similar to sodium metabisulfite, with which it is sometimes used interchangeably. Potassium metabisulfite is generally preferred out of the two as it does not contribute sodium to the diet. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E223','Sodium metabisulfite','Vegan',"Sodium metabisulfite or sodium pyrosulfite (IUPAC spelling; Br. E. sodium metabisulphite or sodium pyrosulphite) is an inorganic compound of chemical formula Na2S2O5. The substance is sometimes referred to as disodium metabisulfite. It is used as a disinfectant, antioxidant, and preservative agent. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E222','Sodium bisulfite (sodium hydrogen sulfite)','Vegan',"Sodium bisulfite (or sodium bisulphite, sodium hydrogen sulfite) is a chemical compound with the chemical formula NaHSO3. Sodium bisulfite is a food additive with E number E222. This salt of bisulfite can be prepared by bubbling sulfur dioxide in a solution of sodium carbonate in water. Sodium bisulfite in contact with chlorine bleach (aqueous solution of sodium hypochlorite) will generate heat and form sodium bisulfate and sodium chloride. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E221','Sodium sulfite','Vegan',"Sodium sulfite (sodium sulphite) is a soluble sodium salt of sulfurous acid (sulfite) with the chemical formula Na2SO3. It is used as a preservative to prevent dried fruit from discoloring, and for preserving meats, and is used in the same way as sodium thiosulfate to convert elemental halogens to their respective hydrohalic acids, in photography and for reducing chlorine levels in pools. In boiler systems, sulfite and bisulfite are the most commonly employed oxygen scavengers used to prevent pitting corrosion. Sodium sulfite is also a byproduct of sulfur dioxide scrubbing, a part of the flue-gas desulfurization process. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E220','Sulfur dioxide','Vegan',"E numbers (\"E\" stands for \"Europe\") are codes for substances used as food additives for use within the European Union (EU) and European Free Trade Association (EFTA). Commonly found on food labels, their safety assessment and approval are the responsibility of the European Food Safety Authority (EFSA). ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E219','Sodium methyl para-hydroxybenzoate','Vegan',"E numbers (\"E\" stands for \"Europe\") are codes for substances used as food additives for use within the European Union (EU) and European Free Trade Association (EFTA). Commonly found on food labels, their safety assessment and approval are the responsibility of the European Food Safety Authority (EFSA). ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E218','Methylparaben (methyl para-hydroxybenzoate)','Vegan',"Methylparaben, also methyl paraben, one of the parabens, is a preservative with the chemical formula CH3(C6H4(OH)COO). It is the methyl ester of p-hydroxybenzoic acid. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E217','Sodium propyl para-hydroxybenzoate','Vegan',"Propylparaben, the n-propyl ester of p-hydroxybenzoic acid, occurs as a natural substance found in many plants and some insects, although it is manufactured synthetically for use in cosmetics, pharmaceuticals, and foods. It is a member of the class of parabens. It is a preservative typically found in many water-based cosmetics, such as creams, lotions, shampoos, and bath products. As a food additive, it has the E number E216. ",'preservative','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E216','Propylparaben (propyl para-hydroxybenzoate)','Vegan',"Propylparaben, the n-propyl ester of p-hydroxybenzoic acid, occurs as a natural substance found in many plants and some insects, although it is manufactured synthetically for use in cosmetics, pharmaceuticals, and foods. It is a member of the class of parabens. It is a preservative typically found in many water-based cosmetics, such as creams, lotions, shampoos, and bath products. As a food additive, it has the E number E216. ",'preservative','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E215','Sodium ethyl para-hydroxybenzoate','Vegan',"Ethylparaben (ethyl para-hydroxybenzoate) is the ethyl ester of p-hydroxybenzoic acid. Its formula is HO-C6H4-CO-O-CH2CH3. It is a member of the class of compounds known as parabens. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E214','Ethylparaben (ethyl para-hydroxybenzoate)','Vegan',"Ethylparaben (ethyl para-hydroxybenzoate) is the ethyl ester of p-hydroxybenzoic acid. Its formula is HO-C6H4-CO-O-CH2CH3. It is a member of the class of compounds known as parabens. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E213','Calcium benzoate','Vegan',"Calcium benzoate refers to the calcium salt of benzoic acid. When used in the food industry as a preservative, its E number is E213 (INS number 213); it is approved for use as a food additive in the EU, USA and Australia and New Zealand. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E212','Potassium benzoate','Vegan',"Potassium benzoate (E212), the potassium salt of benzoic acid, is a food preservative that inhibits the growth of mold, yeast and some bacteria. It works best in low-pH products, below 4.5, where it exists as benzoic acid. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E211','Sodium benzoate','Vegan',"Sodium benzoate is a substance which has the chemical formula C6H5COONa. It is a widely used food preservative, with an E number of E211. It is the sodium salt of benzoic acid and exists in this form when dissolved in water. It can be produced by reacting sodium hydroxide with benzoic acid. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E210','Benzoic acid','Vegan',"Benzoic acid /bɛnˈzoʊ.ɪk/ is a white (or colorless) solid with the formula C6H5CO2H. It is the simplest aromatic carboxylic acid. The name is derived from gum benzoin, which was for a long time its only source. Benzoic acid occurs naturally in many plants and serves as an intermediate in the biosynthesis of many secondary metabolites. Salts of benzoic acid are used as food preservatives. Benzoic acid is an important precursor for the industrial synthesis of many other organic substances. The salts and esters of benzoic acid are known as benzoates /ˈbɛnzoʊ.eɪt/. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E209','Heptyl p-hydroxybenzoate','May or may not be vegan',"Heptylparaben (heptyl p-hydroxybenzoate) is a compound with formula C7H15(C6H4OHCOO). It is a paraben which is the heptyl ester of p-hydroxybenzoic acid. ",'preservative','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E203','Calcium sorbate','Vegan',"Calcium sorbate is the calcium salt of sorbic acid. Calcium sorbate is a polyunsaturated fatty acid salt. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E202','Potassium sorbate','Vegan',"Potassium sorbate is the potassium salt of sorbic acid, chemical formula CH3CH=CH−CH=CH−CO2K. It is a white salt that is very soluble in water (58.2% at 20 °C). It is primarily used as a food preservative (E number 202). Potassium sorbate is effective in a variety of applications including food, wine, and personal-care products. While sorbic acid occurs naturally in some berries, virtually all of the world's production of sorbic acid, from which potassium sorbate is derived, is manufactured synthetically. ",'preservative','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E201','Sodium sorbate','Vegan',"",'preservative','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E200','Sorbic acid','Vegan',"",'preservative','Approved in the EU.']);

    //Thickeners
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E499','Stigmasterol-rich plant sterols','May or may not be vegan',"Stigmasterol-rich plant sterols is a food additive. It is a chemically-defined mixture derived from soybeans that consists of the plant sterols stigmasterol, β-sitosterol, campesterol, and brassicasterol, with stigmasterol representing >85% of the mixture. It is has the E number E499 and is used as a stabiliser in ready-to-freeze alcoholic cocktails. ",'','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E498','Partial polyglycerol esters of polycondensed fatty acids of castor oil','May or may not be vegan',"Sorbitan monopalmitate (SMP) is a food additive, permitted by the EU. It is entry E495 in the E number list of permitted food additives. It is also known under the trade name Span 40. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E497','Polyoxypropylene-polyoxyethylene polymers','May or may not be vegan',"Sorbitan monopalmitate (SMP) is a food additive, permitted by the EU. It is entry E495 in the E number list of permitted food additives. It is also known under the trade name Span 40. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E496','Sorbitan trioleate','May or may not be vegan',"Sorbitan monopalmitate (SMP) is a food additive, permitted by the EU. It is entry E495 in the E number list of permitted food additives. It is also known under the trade name Span 40. ",'emulsifier','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E495','Sorbitan monopalmitate','May or may not be vegan',"Sorbitan monopalmitate (SMP) is a food additive, permitted by the EU. It is entry E495 in the E number list of permitted food additives. It is also known under the trade name Span 40. ",'emulsifier','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E494','Sorbitan monooleate','May or may not be vegan',"Sorbitan monooleate is a food additive with the E number E494. ",'emulsifier','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E493','Sorbitan monolaurate','May or may not be vegan',"Sorbitan monolaurate is a mixture of esters formed from the fatty acid lauric acid and polyols derived from sorbitol, including sorbitan and isosorbide. As a food additive, it is designated with the E number E493. ",'emulsifier','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E492','Sorbitan tristearate','May or may not be vegan',"Sorbitan tristearate is a nonionic surfactant. It is variously used as a dispersing agent, emulsifier, and stabilizer, in food and in aerosol sprays. As a food additive, it has the E number E492. Brand names for polysorbates include Alkest, Canarcel, and Span. The consistency of sorbitan tristearate is waxy; its color is light cream to tan. ",'emulsifier','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E491','Sorbitan monostearate','May or may not be vegan',"Sorbitan monostearate is an ester of sorbitan (a sorbitol derivative) and stearic acid and is sometimes referred to as a synthetic wax. ",'emulsifier','Approved in the EU.']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E490','Propane-1,2-diol','May or may not be vegan',"Propylene glycol (IUPAC name: propane-1,2-diol) is an organic compound with the chemical formula CH3CH(OH)CH2OH. It is a viscous, colorless liquid, which is nearly odorless but possesses a faintly sweet taste. Containing two alcohol groups, it is classed as a diol. It is miscible with a broad range of solvents, including water, acetone, and chloroform. In general, glycols are non-irritating, have very low volatility and very low toxicity. ",'','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E489','Methyl glucoside-coconut oil ester','May or may not be vegan',"Stearyl palmityl tartrate is a derivative of tartaric acid used as an emulsifier. It is produced by esterification of tartaric acid with commercial grade stearyl alcohol, which generally consists of a mixture of the fatty alcohols stearyl and palmityl alcohol. Stearyl palmityl tartrate consists mainly of diesters, with minor amounts of monoester and of unchanged starting materials. ",'emulsifier','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E488','Ethoxylated Mono- and Di-Glycerides','May or may not be vegan',"Stearyl palmityl tartrate is a derivative of tartaric acid used as an emulsifier. It is produced by esterification of tartaric acid with commercial grade stearyl alcohol, which generally consists of a mixture of the fatty alcohols stearyl and palmityl alcohol. Stearyl palmityl tartrate consists mainly of diesters, with minor amounts of monoester and of unchanged starting materials. ",'emulsifier','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E487','Sodium laurylsulfate','May or may not be vegan',"Stearyl palmityl tartrate is a derivative of tartaric acid used as an emulsifier. It is produced by esterification of tartaric acid with commercial grade stearyl alcohol, which generally consists of a mixture of the fatty alcohols stearyl and palmityl alcohol. Stearyl palmityl tartrate consists mainly of diesters, with minor amounts of monoester and of unchanged starting materials. ",'emulsifier','']);
    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E486','Calcium stearoyl fumarate','May or may not be vegan',"Stearyl palmityl tartrate is a derivative of tartaric acid used as an emulsifier. It is produced by esterification of tartaric acid with commercial grade stearyl alcohol, which generally consists of a mixture of the fatty alcohols stearyl and palmityl alcohol. Stearyl palmityl tartrate consists mainly of diesters, with minor amounts of monoester and of unchanged starting materials. ",'emulsifier','']);
