    await db.rawInsert(    'INSERT INTO $table(_title,_name,_suitablefor,_description,_category, _status) VALUES(?,?,?,?,?,?)',    ['E472d','Tartaric acid esters of mono- and diglycerides of fatty acids','May or may not be vegan',"Mono- and diglycerides of fatty acids (E471) refers to a food additive composed of diglycerides and monoglycerides which is used as an emulsifier. This mixture is also sometimes referred to as partial glycerides. ",'emulsifier','Approved in the EU.']);
